import React, { useState, useEffect } from 'react';
import { goodsApi, userApi, disclosureApi, notificationApi } from '../services/api';
import { useNavigate } from 'react-router-dom';
import UserSidebar from '../components/UserSidebar';
import './ProfilePage.css';

const ProfilePage: React.FC = () => {
  const navigate = useNavigate();

  const isAdmin = (() => {
    const token = localStorage.getItem('token');
    if (!token) return false;
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload?.userId === 1;
    } catch {
      return false;
    }
  })();

  const [profile, setProfile] = useState<{ id: number; name: string; nickname?: string; avatar?: string; gender?: number; age?: number; birthDate?: string } | null>(null);
  const [errorMsg, setErrorMsg] = useState<string | null>(null);
  const [isEditing, setIsEditing] = useState<boolean>(false); // ÊéßÂà∂ÁºñËæëÁä∂ÊÄÅ
  const [editForm, setEditForm] = useState({
    name: '',
    nickname: '',
    gender: 0,
    age: 0,
    birthDate: ''
  });

  const [featureCounts, setFeatureCounts] = useState([
    { id: 1, name: '„Éû„Ç§„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥', icon: '‚ù§Ô∏è', count: 0 },
    { id: 2, name: 'Èñ≤Ë¶ßÂ±•Ê≠¥', icon: 'üïí', count: 0 },
    { id: 4, name: '„Éû„Ç§„Éí„É≥„Éà', icon: 'üì¢', count: 0 },
    { id: 5, name: 'ÈÄöÁü•', icon: 'üîî', count: 0 }
  ]);

  // „É¶„Éº„Ç∂„Éº„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      // Ëé∑ÂèñÁî®Êà∑Âü∫Êú¨‰ø°ÊÅØ
      const meRes = await userApi.getProfile();
      if (meRes?.data?.code !== 200) {
        setErrorMsg(meRes?.data?.message || '„É¶„Éº„Ç∂„ÉºÊÉÖÂ†±„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
        console.warn('„É¶„Éº„Ç∂„ÉºÊÉÖÂ†±ÂèñÂæóAPI„Ç®„É©„Éº:', meRes?.data?.message);
        return;
      }

      const me = meRes?.data?.data;
      if (!me) {
        setErrorMsg('„É¶„Éº„Ç∂„ÉºÊÉÖÂ†±„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì');
        return;
      }

      setProfile({
        id: me.id,
        name: me.name || `„É¶„Éº„Ç∂„Éº${me.id}`,
        nickname: me.nickname,
        avatar: me.avatar,
        gender: me.gender,
        age: me.age,
        birthDate: me.birthDate ? new Date(me.birthDate).toISOString().split('T')[0] : '' // Â∞ÜÊó•ÊúüËΩ¨Êç¢‰∏∫ YYYY-MM-DD Ê†ºÂºè
      });
      // ÂàùÂßãÂåñÁºñËæëË°®Âçï
      setEditForm({
        name: me.name || `„É¶„Éº„Ç∂„Éº${me.id}`,
        nickname: me.nickname || '',
        gender: me.gender || 0,
        age: me.age || 0,
        birthDate: me.birthDate ? new Date(me.birthDate).toISOString().split('T')[0] : ''
      });

      // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥ÂïÜÂìÅÊï∞„ÅÆÂèñÂæó
      try {
        const collectResponse = await goodsApi.getMyCollect(1, 100);
        if (collectResponse?.data?.code === 200) {
          const collectData = collectResponse?.data?.data;
          const collectCount = Array.isArray(collectData) ? collectData.length : (collectData?.records?.length || 0);

          // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Êï∞„ÅÆÊõ¥Êñ∞
          setFeatureCounts(prev =>
            prev.map(item =>
              item.id === 1 ? { ...item, count: collectCount } : item
            )
          );
        }
      } catch (collectError) {
        console.warn('„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥ÊÉÖÂ†±ÂèñÂæó„Ç®„É©„Éº:', collectError);
        // ‰∏çÊòæÁ§∫Êî∂ÈõÜÈîôËØØÔºå‰ª•ÂÖçÂΩ±Èüø‰∏ªÊµÅÁ®ã
      }

      // Èñ≤Ë¶ßÂ±•Ê≠¥„ÅÆÂèñÂæó
      try {
        const historyResponse = await userApi.getHistory(1, 100);
        if (historyResponse?.data?.code === 200) {
          const historyData = historyResponse?.data?.data;
          const historyCount = Array.isArray(historyData) ? historyData.length : (historyData?.records?.length || 0);

          setFeatureCounts(prev =>
            prev.map(item =>
              item.id === 2 ? { ...item, count: historyCount } : item
            )
          );
        }
      } catch (historyError) {
        console.warn('Èñ≤Ë¶ßÂ±•Ê≠¥ÂèñÂæó„Ç®„É©„Éº:', historyError);
        // ‰∏çÊòæÁ§∫ÊµèËßàÂéÜÂè≤ÈîôËØØÔºå‰ª•ÂÖçÂΩ±Èüø‰∏ªÊµÅÁ®ã
      }

      try {
        const tipRes = await disclosureApi.getMyDisclosure(1, 100);
        if (tipRes?.data?.code === 200) {
          const tipData = tipRes?.data?.data;
          const tipCount = Array.isArray(tipData) ? tipData.length : (tipData?.records?.length || 0);

          setFeatureCounts(prev =>
            prev.map(item =>
              item.id === 4 ? { ...item, count: tipCount } : item
            )
          );
        }
      } catch (tipError) {
        console.warn('ÊäïÁ®øÊÉÖÂ†±ÂèñÂæó„Ç®„É©„Éº:', tipError);
        // ‰∏çÊòæÁ§∫ÊäïÁ®øÊÉÖÂ†±ÈîôËØØÔºå‰ª•ÂÖçÂΩ±Èüø‰∏ªÊµÅÁ®ã
      }

      // ÈÄöÁü•ÂèñÂæó
      try {
        const notiRes = await notificationApi.getMyNotifications();
        if (notiRes?.data?.code === 200) {
          const notiList = notiRes?.data?.data || [];
          const unreadList = notiList.filter((n: { isRead: number }) => n.isRead === 0);

          let notiCount = 0;
          if (unreadList.length > 0) {
            // Ê§úÊü•ÊúÄÊñ∞„É°„ÉÉ„Çª„Éº„Ç∏ÊôÇÈñìÊòØÂê¶Êôö‰∫é‰∏äÊ¨°„ÇØ„É™„ÉÉ„ÇØÊôÇÈñì
            const latestMsg = unreadList[0]; // ÂÅáËÆæÂêéÁ´ØÂ∑≤ÊåâÊôÇÈñìÂÄíÂ∫èËøîÂõû
            const latestTime = new Date(latestMsg.createTime).getTime();
            const lastCheck = parseInt(localStorage.getItem('last_notification_check_time') || '0');

            if (latestTime > lastCheck) {
              notiCount = unreadList.length;
            }
          }

          setFeatureCounts(prev =>
            prev.map(item => {
              if (item.id === 5) return { ...item, count: notiCount };
              return item;
            })
          );
        }
      } catch (notiError) {
        console.warn('ÈÄöÁü•ÊÉÖÂ†±ÂèñÂæó„Ç®„É©„Éº:', notiError);
        // ‰∏çÊòæÁ§∫ÈÄöÁü•ÈîôËØØÔºå‰ª•ÂÖçÂΩ±Èüø‰∏ªÊµÅÁ®ã
      }
    } catch (error: any) {
      console.error('„É¶„Éº„Ç∂„Éº„Éá„Éº„ÇøÂÖ®‰Ωì„ÅÆË™≠„ÅøËæº„Åø„Ç®„É©„Éº:', error);

      // Ê§úÊü•ÈîôËØØÁ±ªÂûãÂπ∂Êèê‰æõÊõ¥Á≤æÁ°Æ„ÅÆÈîôËØØÊÉÖÂ†±
      if (error.response) {
        // ÊúçÂä°Âô®ÂìçÂ∫î‰∫ÜÈîôËØØÁä∂ÊÄÅÁ†Å
        if (error.response.status === 401) {
          // 401ÈîôËØØÂèØËÉΩÊòØÂõ†‰∏∫JWTËøáÊúüÔºåÂ∞ùËØïÂà∑Êñ∞È°µÈù¢‰ª•ÈáçÊñ∞Ëé∑Âèñ‰ª§Áâå
          setErrorMsg('„É≠„Ç∞„Ç§„É≥ÊÉÖÂ†±„ÅÆÊúüÈôê„ÅåÂàá„Çå„Å¶„ÅÑ„Åæ„Åô„ÄÇ„Éö„Éº„Ç∏„ÇíÂÜçË™≠„ÅøËæº„Åø„Åó„Åæ„Åô„ÄÇ');
          setTimeout(() => {
            window.location.reload();
          }, 2000);
        } else if (error.response.status === 403) {
          setErrorMsg('„Ç¢„ÇØ„Çª„ÇπÊ®©Èôê„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ');
        } else if (error.response.status >= 500) {
          setErrorMsg('„Çµ„Éº„Éê„Éº„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü„ÄÇ„Åó„Å∞„Çâ„Åè„Åó„Å¶„Åã„ÇâÂÜçÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
        } else {
          setErrorMsg(`${error.response.data?.message || '„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ'} (${error.response.status})`);
        }
      } else if (error.request) {
        // ËØ∑Ê±ÇÂ∑≤ÂèëÂá∫‰ΩÜÊ≤°ÊúâÊî∂Âà∞ÂìçÂ∫î
        setErrorMsg('„Éç„ÉÉ„Éà„ÉØ„Éº„ÇØÊé•Á∂ö„Ç®„É©„Éº„ÄÇ„Ç§„É≥„Çø„Éº„Éç„ÉÉ„ÉàÊé•Á∂ö„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
      } else {
        // ÂÖ∂‰ªñÈîôËØØ
        setErrorMsg('‰∫àÊúü„Åõ„Å¨„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü„ÄÇ');
      }
    }
  };

  const handleFeatureClick = (id: number) => {
    switch (id) {
      case 1:
        navigate('/my-collection');
        break;
      case 2:
        navigate('/browse-history');
        break;
      case 4:
        navigate('/my-tip');
        break;
      case 5:
        // Êõ¥Êñ∞ÊúÄÂêéÊü•ÁúãÊó∂Èó¥ÔºåÊ∂àÈô§Á∫¢ÁÇπ
        localStorage.setItem('last_notification_check_time', Date.now().toString());
        navigate('/notifications');
        break;
      default:
        break;
    }
  };

  const toggleEdit = () => {
    if (isEditing) {
      // ÂèñÊ∂àÁºñËæëÔºåÊÅ¢Â§çÂéüÂßãÊï∞ÊçÆ
      if (profile) {
        setEditForm({
          name: profile.name || `„É¶„Éº„Ç∂„Éº${profile.id}`,
          nickname: profile.nickname || '',
          gender: profile.gender || 0,
          age: profile.age || 0,
          birthDate: profile.birthDate || ''
        });
      }
    }
    setIsEditing(!isEditing);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: name === 'age' ? Number(value) : value
    }));
  };

  const handleSave = async () => {
    if (!profile) return;

    try {
      // Ë∞ÉÁî®APIÊõ¥Êñ∞Áî®Êà∑ËµÑÊñô
      const response = await userApi.updateProfile({
        nickname: editForm.nickname,
        gender: editForm.gender,
        age: editForm.age,
        birthDate: editForm.birthDate
      });

      if (response.data.code === 200) {
        // Êõ¥Êñ∞ÊàêÂäüÔºåÊõ¥Êñ∞Êú¨Âú∞Áä∂ÊÄÅ
        setProfile({
          ...profile,
          nickname: editForm.nickname,
          gender: editForm.gender,
          age: editForm.age,
          birthDate: editForm.birthDate
        });
        setIsEditing(false);
        alert('„Éó„É≠„Éï„Ç£„Éº„É´„ÅåÊõ¥Êñ∞„Åï„Çå„Åæ„Åó„ÅüÔºÅ');
      } else {
        setErrorMsg(response.data.message || 'Êõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }
    } catch (error) {
      setErrorMsg('„Éó„É≠„Éï„Ç£„Éº„É´„ÅÆÊõ¥Êñ∞‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü');
      console.error('„Éó„É≠„Éï„Ç£„Éº„É´Êõ¥Êñ∞„Ç®„É©„Éº:', error);
    }
  };

  // ÂÅ•Â∫∑Ê£ÄÊü•ÂáΩÊï∞Ë¢´ÁßªÈô§

  return (
    <div className="profile-page-wrapper" style={{ display: 'flex', maxWidth: '1200px', margin: '20px auto', gap: '20px', padding: '0 15px' }}>
      <UserSidebar />
      <div className="profile-page" style={{ flex: 1, padding: 0, margin: 0, maxWidth: 'none', minHeight: 'auto' }}>
        <div className="profile-header">
          <div className="user-info">
            {!isEditing ? (
              <>
                <h2>{profile?.nickname || profile?.name || '„É¶„Éº„Ç∂„Éº'}</h2>
                <div className="user-details">
                  {profile?.gender !== undefined && profile.gender !== null && (
                    <span>ÊÄßÂà•: {profile.gender === 1 ? 'Áî∑ÊÄß' : profile.gender === 2 ? 'Â•≥ÊÄß' : 'Êú™Ë®≠ÂÆö'}</span>
                  )}
                  {profile?.age && <span>, Âπ¥ÈΩ¢: {profile.age}</span>}
                  {profile?.birthDate && <span>, ÁîüÂπ¥ÊúàÊó•: {profile.birthDate}</span>}
                </div>
              </>
            ) : (
              <div className="edit-form">
                <input
                  type="text"
                  name="nickname"
                  value={editForm.nickname}
                  onChange={handleInputChange}
                  placeholder="„Éã„ÉÉ„ÇØ„Éç„Éº„É†"
                  className="edit-input"
                />
                <select
                  name="gender"
                  value={editForm.gender}
                  onChange={handleInputChange}
                  className="edit-input"
                >
                  <option value={0}>Êú™Ë®≠ÂÆö</option>
                  <option value={1}>Áî∑ÊÄß</option>
                  <option value={2}>Â•≥ÊÄß</option>
                </select>
                <input
                  type="number"
                  name="age"
                  value={editForm.age || ''}
                  onChange={handleInputChange}
                  placeholder="Âπ¥ÈΩ¢"
                  className="edit-input"
                  min="1"
                  max="120"
                />
                <input
                  type="date"
                  name="birthDate"
                  value={editForm.birthDate}
                  onChange={handleInputChange}
                  className="edit-input"
                />
              </div>
            )}
          </div>
          <div className="edit-button-group">
            {!isEditing ? (
              <button className="edit-profile-btn" onClick={toggleEdit}>„Éó„É≠„Éï„Ç£„Éº„É´„ÇíÁ∑®ÈõÜ</button>
            ) : (
              <>
                <button className="save-profile-btn" onClick={handleSave}>‰øùÂ≠ò</button>
                <button className="cancel-edit-btn" onClick={toggleEdit}>„Ç≠„É£„É≥„Çª„É´</button>
              </>
            )}
          </div>
        </div>

        <div className="profile-features">
          {errorMsg && (
            <div style={{ background: '#fff3cd', border: '1px solid #ffeeba', color: '#856404', padding: '10px 12px', marginBottom: '10px', borderRadius: '2px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <span>{errorMsg}</span>
              <button
                onClick={loadUserData}
                style={{ background: '#ffc107', border: '1px solid #d39e00', color: '#856404', padding: '4px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '12px' }}
              >
                ÂÜçË™≠„ÅøËæº„Åø
              </button>
            </div>
          )}
          {featureCounts.map(feature => (
            <div
              key={feature.id}
              className="feature-item"
              onClick={() => handleFeatureClick(feature.id)}
              style={{ cursor: 'pointer' }}
            >
              <div className="feature-icon">{feature.icon}</div>
              <div className="feature-name">{feature.name}</div>
              {feature.id === 5 && feature.count > 0 && <div className="feature-count">{feature.count}</div>}
            </div>
          ))}
        </div>

        {isAdmin && (
          <div className="settings-section">
            <div className="setting-item" onClick={() => navigate('/admin/disclosures')} style={{ cursor: 'pointer' }}>
              <span>ÁÆ°ÁêÜËÄÖÔºöÊäïÁ®øÂØ©Êüª</span>
              <span className="arrow">‚Ä∫</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
