import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import './ComparePage.css';
import { goodsApi } from '../services/api';

interface Product {
  goodsId?: number;
  goodsName: string;
  goodsPrice: number;
  goodsLink: string;
  imgUrl?: string;
  mallType: number;
}

interface CompareGroup {
  goodsName: string;
  goodsList: Product[];
  lowestPrice: number | null;
  lowestPlatform?: string;
}

const ComparePage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [query, setQuery] = useState(searchParams.get('query') || '');
  const [compareGroups, setCompareGroups] = useState<CompareGroup[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // 実際のAPI呼び出し
  useEffect(() => {
    if (query.trim()) {
      searchProducts(query);
    }
  }, [query]);

  const searchProducts = async (searchQuery: string) => {
    setLoading(true);
    setError(null);

    try {
      console.log('Calling API: goodsApi.compareGoods with query:', searchQuery); // デバッグ用ログ

      // 使用api.ts中定义的API服务
      const response = await goodsApi.compareGoods(searchQuery);

      console.log('API Response:', response.data); // デバッグ用ログ

      let responseData = [];
      if (response.data && response.data.data) {
        responseData = Array.isArray(response.data.data) ? response.data.data : [response.data.data];
      } else {
        responseData = [];
      }

      console.log('Raw Response Data:', responseData); // デバッグ用ログ

      // レスポンスデータを処理
      let processedData = [];

      if (responseData.length > 0) {
        // APIが返すグループデータを適切に処理
        processedData = responseData.map((group: any) => {
          // グループ内の商品リストを処理
          const goodsList = Array.isArray(group.goodsList) ? group.goodsList : [];

          // 最低価格とプラットフォームを特定
          let lowestPrice = null;
          let lowestPlatform = '不明';

          // 最低価格を計算
          if (goodsList.length > 0) {
            const lowestProduct = goodsList.reduce((lowest: any, current: any) => 
              current.goodsPrice < lowest.goodsPrice ? current : lowest, goodsList[0]);

            lowestPrice = lowestProduct.goodsPrice;
            lowestPlatform = getPlatformName(lowestProduct.mallType);
          }

          return {
            goodsName: group.goodsName || '不明な商品',
            goodsList: goodsList.map((product: any) => ({
              goodsId: product.goodsId,
              goodsName: product.goodsName,
              goodsPrice: product.goodsPrice,
              goodsLink: product.goodsLink,
              imgUrl: product.imgUrl,
              mallType: product.mallType
            })),
            lowestPrice: lowestPrice,
            lowestPlatform: lowestPlatform
          };
        });

        console.log('Processed grouped data:', processedData); // デバッグ用ログ
      }

      console.log('Final processed data:', processedData); // デバッグ用ログ

      setCompareGroups(processedData);
      setLoading(false);
    } catch (err) {
      setError('検索に失敗しました。後ほど再試行してください');
      setLoading(false);
      console.error(err);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (query.trim()) {
      searchProducts(query);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'ヤフー';
      case 30: return 'タオバオ';
      default: return '不明なプラットフォーム';
    }
  };

  const getPlatformColor = (mallType: number): string => {
    switch (mallType) {
      case 10: return '#ff4400'; // 楽天
      case 20: return '#00a0e9'; // ヤフー
      case 30: return '#ff6600'; // タオバオ
      default: return '#666';
    }
  };

  const isBestPrice = (product: Product, group: CompareGroup) => {
    return group.lowestPrice !== null && product.goodsPrice === group.lowestPrice;
  };

  return (
    <div className="compare-page">
      <div className="search-section">
        <h1>商品価格比較</h1>
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="商品名を入力して価格比較..."
            className="search-input"
          />
          <button type="submit" className="search-button">検索</button>
        </form>
      </div>

      {loading && <div className="loading">検索中...</div>}
      {error && <div className="error">{error}</div>}

      {!loading && !error && compareGroups.length > 0 && (
        <div className="compare-results">
          <h2>価格比較結果</h2>
          {compareGroups.map((group, index) => (
            <div key={index} className="compare-group">
              <div className="group-header">
                <h3>{group.goodsName}</h3>
                {group.lowestPrice !== null && (
                  <div className="lowest-price">
                    <span>最安値: </span>
                    <span className="price">¥{group.lowestPrice}</span>
                    <span className="platform">({group.lowestPlatform})</span>
                  </div>
                )}
              </div>

              <div className="product-list">
                {group.goodsList.map((product, productIndex) => (
                  <div 
                    key={productIndex} 
                    className={`product-item ${isBestPrice(product, group) ? 'best-item' : ''}`}
                  >
                    <div className="product-info">
                      <div className="product-image">
                        <img 
                          src={product.imgUrl || 'https://picsum.photos/100/100?random=product'} 
                          alt={product.goodsName} 
                        />
                      </div>
                      <div className="product-details">
                        <h4>{product.goodsName}</h4>
                        <div className="price" style={{ color: getPlatformColor(product.mallType) }}>
                          ¥{product.goodsPrice}
                        </div>
                        <div className="platform" style={{ color: getPlatformColor(product.mallType) }}>
                          {getPlatformName(product.mallType)}
                        </div>
                      </div>
                    </div>
                    <div className="product-actions">
                      <a 
                        href={product.goodsLink} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className={`buy-button ${isBestPrice(product, group) ? 'best-price' : ''}`}
                      >
                        {isBestPrice(product, group) ? '最安値で購入' : '購入する'}
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {!loading && !error && compareGroups.length === 0 && query && (
        <div className="no-results">関連商品が見つかりません</div>
      )}
    </div>
  );
};

export default ComparePage;
