import React, { useState } from 'react';
import './MyTipPage.css';

interface Tip {
  id: number;
  title: string;
  content: string;
  time: string;
  status: '審査中' | '承認済' | '却下';
  reward?: number;
}

const MyTipPage: React.FC = () => {
  const [tips, setTips] = useState<Tip[]>([
    {
      id: 1,
      title: 'iPhone 15価格優遇',
      content: '某某プラットフォームでiPhone 15が大幅に割引され、通常より500元安い',
      time: '2023-12-24 10:30',
      status: '承認済',
      reward: 10
    },
    {
      id: 2,
      title: 'MacBook Air限定割引',
      content: 'ダブル12期間中、MacBook Airに割引活動があり、注目価値あり',
      time: '2023-12-23 15:45',
      status: '審査中'
    },
    {
      id: 3,
      title: 'AirPodsプロモーション情報',
      content: '某某商家のAirPodsプロモーション、公式サイトより安い',
      time: '2023-12-22 09:20',
      status: '却下'
    }
  ]);

  const submitTip = () => {
    // 新しいヒントの投稿をシミュレート
    const newTip: Tip = {
      id: tips.length + 1,
      title: '新ヒント',
      content: '新しいヒントの内容',
      time: new Date().toLocaleString(),
      status: '審査中'
    };
    setTips([newTip, ...tips]);
  };

  return (
    <div className="my-tip-page">
      <div className="header">
        <h2>マイヒント</h2>
        <button className="submit-button" onClick={submitTip}>
          ヒントを投稿
        </button>
      </div>

      {tips.length === 0 ? (
        <div className="no-tips">ヒント記録がありません</div>
      ) : (
        <div className="tip-list">
          {tips.map((tip) => (
            <div key={tip.id} className="tip-item">
              <div className="tip-header">
                <h3>{tip.title}</h3>
                <span className={`status status-${tip.status}`}>{tip.status}</span>
              </div>
              <div className="tip-content">{tip.content}</div>
              <div className="tip-footer">
                <span className="time">{tip.time}</span>
                {tip.reward && <span className="reward">報酬: ¥{tip.reward}</span>}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyTipPage;
