import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import './ComparePage.css';
import { goodsApi } from '../services/api';

interface Product {
  goodsId?: number;
  goodsName: string;
  goodsPrice: number;
  goodsLink: string;
  imgUrl?: string;
  mallType: number;
}

interface CompareGroup {
  goodsName: string;
  goodsList: Product[];
  lowestPrice: number | null;
  lowestPlatform?: string;
}

const ComparePage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [query, setQuery] = useState(searchParams.get('query') || '');
  const [compareGroups, setCompareGroups] = useState<CompareGroup[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [sortOption, setSortOption] = useState('price_asc');
  const [selectedPlatforms, setSelectedPlatforms] = useState<number[]>([]);
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);
  const [selectedKeys, setSelectedKeys] = useState<string[]>([]);
  const [selectedBrands, setSelectedBrands] = useState<string[]>([]);
  const [selectedSeries, setSelectedSeries] = useState<string[]>([]);
  const [selectedModels, setSelectedModels] = useState<string[]>([]);
  const [selectedStorage, setSelectedStorage] = useState<string[]>([]);
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [trendOpen, setTrendOpen] = useState(false);
  const [trendLabels, setTrendLabels] = useState<string[]>([]);
  const [trendSeries, setTrendSeries] = useState<number[]>([]);
  const [trendTitle, setTrendTitle] = useState('');

  // 仅在页面加载时，如果有URL参数，则执行搜索
  useEffect(() => {
    if (query.trim()) {
      searchProducts(query);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const searchProducts = async (searchQuery: string) => {
    if (!searchQuery.trim()) return; // 如果查询为空则不执行搜索

    setLoading(true);
    setError(null);

    try {
      console.log('Calling API: goodsApi.compareGoods with query:', searchQuery); // デバッグ用ログ

      // 使用api.ts中定义的API服务
      const response = await goodsApi.compareGoods(searchQuery);

      console.log('API Response:', response.data); // デバッグ用ログ

      let responseData = [];
      if (response.data && response.data.data) {
        responseData = Array.isArray(response.data.data) ? response.data.data : [response.data.data];
      } else {
        responseData = [];
      }

      console.log('Raw Response Data:', responseData); // デバッグ用ログ

      // レスポンスデータを処理
      let processedData = [];

      if (responseData.length > 0) {
        // APIが返すグループデータを適切に処理
        processedData = responseData.map((group: any) => {
          // グループ内の商品リストを処理
          const goodsList = Array.isArray(group.goodsList) ? group.goodsList : [];

          // 最低価格とプラットフォームを特定
          let lowestPrice = null;
          let lowestPlatform = '不明';

          // 最低価格を計算
          if (goodsList.length > 0) {
            const lowestProduct = goodsList.reduce((lowest: any, current: any) => 
              current.goodsPrice < lowest.goodsPrice ? current : lowest, goodsList[0]);

            lowestPrice = lowestProduct.goodsPrice;
            lowestPlatform = getPlatformName(lowestProduct.mallType);
          }

          return {
            goodsName: group.goodsName || '不明な商品',
            goodsList: goodsList.map((product: any) => ({
              goodsId: product.goodsId,
              goodsName: product.goodsName,
              goodsPrice: product.goodsPrice,
              goodsLink: product.goodsLink,
              imgUrl: product.imgUrl,
              mallType: product.mallType
            })),
            lowestPrice: lowestPrice,
            lowestPlatform: lowestPlatform
          };
        });

        console.log('Processed grouped data:', processedData); // デバッグ用ログ
      }

      console.log('Final processed data:', processedData); // デバッグ用ログ

      setCompareGroups(processedData);
      setLoading(false);
    } catch (err) {
      setError('検索に失敗しました。後ほど再試行してください');
      setLoading(false);
      console.error(err);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (query.trim()) {
      searchProducts(query);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'ヤフー';
      case 30: return 'タオバオ';
      default: return '不明なプラットフォーム';
    }
  };

  const getPlatformBadgeClass = (mallType: number): string => {
    switch (mallType) {
      case 10: return 'platform-badge rakuten';
      case 20: return 'platform-badge yahoo';
      case 30: return 'platform-badge taobao';
      default: return 'platform-badge unknown';
    }
  };

  const allProducts: Product[] = compareGroups.flatMap(group => group.goodsList);
  const knownBrands = ['Apple','Samsung','Sony','Xiaomi','Huawei','Lenovo','Dell','HP','ASUS','Acer','Nintendo','Microsoft','Canon','Nikon'];
  const parseFacets = (name: string) => {
    const n = name || '';
    const brand = knownBrands.find(b => new RegExp(b, 'i').test(n)) || '';
    const seriesMatch = n.match(/(iPhone|MacBook|iPad|Galaxy|ThinkPad|Surface|PlayStation|Switch|WH\-\d{4}XM\d|RX\s?\d{3,}|EOS\s?\w+)/i);
    const series = seriesMatch ? seriesMatch[1] : '';
    const modelMatch = n.match(/([A-Za-z]{1,3}\-?\d{2,4}[A-Za-z]?|\bPro Max\b|\bPro\b|\bUltra\b|\bPlus\b|\bAir\b|\bMini\b)/i);
    const model = modelMatch ? modelMatch[1] : '';
    const storageMatch = n.match(/(\b\d{2,4}\s?(GB|TB)\b)/i);
    const storage = storageMatch ? storageMatch[1].replace(/\s+/g,'') : '';
    const colorMatch = n.match(/\b(Black|White|Silver|Blue|Red|Green|Gold|Pink|Gray|Space Gray|Midnight)\b/i);
    const color = colorMatch ? colorMatch[1] : '';
    const sizeMatch = n.match(/(\b\d{1,2}(?:\.\d)?\s?(?:吋|インチ|"|英寸)\b)/);
    const size = sizeMatch ? sizeMatch[1] : '';
    return { brand, series, model, storage, color, size };
  };
  const facetIndex = allProducts.map(p => parseFacets(p.goodsName));
  const brands = Array.from(new Set(facetIndex.map(f => f.brand).filter(Boolean)));
  const series = Array.from(new Set(facetIndex.map(f => f.series).filter(Boolean)));
  const models = Array.from(new Set(facetIndex.map(f => f.model).filter(Boolean)));
  const storages = Array.from(new Set(facetIndex.map(f => f.storage).filter(Boolean)));
  const colors = Array.from(new Set(facetIndex.map(f => f.color).filter(Boolean)));
  const sizes = Array.from(new Set(facetIndex.map(f => f.size).filter(Boolean)));
  const parseCategory = (name: string) => {
    const n = (name || '').toLowerCase();
    if (/手机壳|ケース|保护壳|カバー/.test(n)) return '手机壳';
    if (/スマートフォン|手机|携帯|iphone|galaxy|xiaomi|huawei/.test(n)) return '手机';
    if (/パソコン|笔记本|ノート|laptop|macbook|thinkpad|surface|pc/.test(n)) return '电脑';
    if (/家电|冷蔵庫|电视|电视机|洗衣机|空调|エアコン|レンジ|掃除機|电饭煲|加湿器/.test(n)) return '家电';
    if (/カメラ|相机|単眼|镜头|レンズ|eos|nikon|canon/.test(n)) return '相机';
    if (/耳机|ヘッドホン|イヤホン|airpods|xm\d|sony wh/.test(n)) return '耳机';
    if (/switch|任天堂|playstation|ps\d|游戏机|游戏/.test(n)) return '游戏';
    if (/显示器|モニター|monitor|液晶|oled/.test(n)) return '显示器';
    if (/ssd|hdd|硬盘|存储|storage|micro\s?sd|内存条|内存/.test(n)) return '存储';
    return '其他';
  };
  const categoriesAll = Array.from(new Set(allProducts.map(p => parseCategory(p.goodsName)).filter(Boolean)));
  const filteredProducts: Product[] = allProducts.filter(p => {
    const platformOk = selectedPlatforms.length === 0 || selectedPlatforms.includes(p.mallType);
    const minOk = minPrice === '' || (!isNaN(Number(minPrice)) && p.goodsPrice >= Number(minPrice));
    const maxOk = maxPrice === '' || (!isNaN(Number(maxPrice)) && p.goodsPrice <= Number(maxPrice));
    const f = parseFacets(p.goodsName);
    const brandOk = selectedBrands.length === 0 || (f.brand && selectedBrands.includes(f.brand));
    const seriesOk = selectedSeries.length === 0 || (f.series && selectedSeries.includes(f.series));
    const modelOk = selectedModels.length === 0 || (f.model && selectedModels.includes(f.model));
    const storageOk = selectedStorage.length === 0 || (f.storage && selectedStorage.includes(f.storage));
    const colorOk = selectedColors.length === 0 || (f.color && selectedColors.includes(f.color));
    const sizeOk = selectedSizes.length === 0 || (f.size && selectedSizes.includes(f.size));
    const category = parseCategory(p.goodsName);
    const categoryOk = selectedCategories.length === 0 || selectedCategories.includes(category);
    return platformOk && minOk && maxOk && brandOk && seriesOk && modelOk && storageOk && colorOk && sizeOk && categoryOk;
  });
  const sortedProducts: Product[] = [...filteredProducts].sort((a, b) => {
    if (sortOption === 'price_asc') return a.goodsPrice - b.goodsPrice;
    if (sortOption === 'price_desc') return b.goodsPrice - a.goodsPrice;
    if (sortOption === 'platform') return a.mallType - b.mallType;
    if (sortOption === 'name') return a.goodsName.localeCompare(b.goodsName);
    return 0;
  });
  const togglePlatform = (mt: number) => {
    setSelectedPlatforms(prev => prev.includes(mt) ? prev.filter(x => x !== mt) : [...prev, mt]);
  };

  const totalPages = Math.max(1, Math.ceil(sortedProducts.length / pageSize));
  const currentPageProducts = sortedProducts.slice((currentPage - 1) * pageSize, currentPage * pageSize);
  const goPrev = () => setCurrentPage(p => Math.max(1, p - 1));
  const goNext = () => setCurrentPage(p => Math.min(totalPages, p + 1));
  const setPage = (p: number) => setCurrentPage(Math.min(Math.max(1, p), totalPages));
  const onChangePageSize = (size: number) => { setPageSize(size); setCurrentPage(1); };

  const getSelectKey = (p: Product) => {
    if (p.goodsId) return `id:${p.goodsId}`;
    const base = `${p.mallType}-${p.goodsLink || ''}-${p.goodsName}`;
    return `k:${base}`;
  };
  const isSelected = (key: string) => selectedKeys.includes(key);
  const toggleSelect = (p: Product) => {
    const key = getSelectKey(p);
    setSelectedKeys(prev => prev.includes(key) ? prev.filter(x => x !== key) : [...prev, key]);
  };

  const getSellerFromLink = (url?: string) => {
    if (!url) return '不明な店铺';
    try {
      const u = new URL(url.startsWith('http') ? url : ('https://' + url.replace(/^\/\//, '')));
      return u.hostname.replace(/^www\./, '');
    } catch {
      return '不明な店铺';
    }
  };

  return (
    <div className="compare-page">
      <div className="search-section">
        <h1>商品価格比較</h1>
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)} // 仅更新状态，不触发搜索
            placeholder="商品名を入力して価格比較..."
            className="search-input"
          />
          <button type="submit" className="search-button">搜索</button>
        </form>
      </div>

      {loading && <div className="loading">検索中...</div>}
      {error && <div className="error">{error}</div>}

      {!loading && !error && allProducts.length > 0 && (
        <div className="compare-results">
          <h2>価格比較結果</h2>
          <div className="controls">
            <div className="control-group">
              <label>排序</label>
              <select value={sortOption} onChange={(e) => setSortOption(e.target.value)} className="sort-select">
                <option value="price_asc">价格升序</option>
                <option value="price_desc">价格降序</option>
                <option value="platform">平台</option>
                <option value="name">名称</option>
              </select>
            </div>
            {brands.length > 0 && (
              <div className="control-group">
                <label>品牌</label>
                <div className="platform-options">
                  {brands.map(b => (
                    <button type="button" key={b} className={selectedBrands.includes(b) ? 'active' : ''} onClick={() => setSelectedBrands(prev => prev.includes(b) ? prev.filter(x=>x!==b) : [...prev,b])}>{b}</button>
                  ))}
                </div>
              </div>
            )}
            {series.length > 0 && (
              <div className="control-group">
                <label>系列</label>
                <div className="platform-options">
                  {series.map(s => (
                    <button type="button" key={s} className={selectedSeries.includes(s) ? 'active' : ''} onClick={() => setSelectedSeries(prev => prev.includes(s) ? prev.filter(x=>x!==s) : [...prev,s])}>{s}</button>
                  ))}
                </div>
              </div>
            )}
            {models.length > 0 && (
              <div className="control-group">
                <label>型号</label>
                <div className="platform-options">
                  {models.map(m => (
                    <button type="button" key={m} className={selectedModels.includes(m) ? 'active' : ''} onClick={() => setSelectedModels(prev => prev.includes(m) ? prev.filter(x=>x!==m) : [...prev,m])}>{m}</button>
                  ))}
                </div>
              </div>
            )}
            {categoriesAll.length > 0 && (
              <div className="control-group">
                <label>类别</label>
                <div className="platform-options">
                  {categoriesAll.map(c => (
                    <button type="button" key={c} className={selectedCategories.includes(c) ? 'active' : ''} onClick={() => setSelectedCategories(prev => prev.includes(c) ? prev.filter(x=>x!==c) : [...prev,c])}>{c}</button>
                  ))}
                </div>
              </div>
            )}
            <div className="control-group platform-filters">
              <label>平台</label>
              <div className="platform-options">
                <button type="button" className={selectedPlatforms.includes(10) ? 'active' : ''} onClick={() => togglePlatform(10)}>楽天</button>
                <button type="button" className={selectedPlatforms.includes(20) ? 'active' : ''} onClick={() => togglePlatform(20)}>ヤフー</button>
                <button type="button" className={selectedPlatforms.includes(30) ? 'active' : ''} onClick={() => togglePlatform(30)}>タオバオ</button>
              </div>
            </div>
            {(storages.length > 0 || colors.length > 0 || sizes.length > 0) && (
              <div className="control-group">
                <label>规格</label>
                <div className="platform-options">
                  {storages.map(s => (
                    <button type="button" key={s} className={selectedStorage.includes(s) ? 'active' : ''} onClick={() => setSelectedStorage(prev => prev.includes(s) ? prev.filter(x=>x!==s) : [...prev,s])}>{s}</button>
                  ))}
                  {colors.map(c => (
                    <button type="button" key={c} className={selectedColors.includes(c) ? 'active' : ''} onClick={() => setSelectedColors(prev => prev.includes(c) ? prev.filter(x=>x!==c) : [...prev,c])}>{c}</button>
                  ))}
                  {sizes.map(siz => (
                    <button type="button" key={siz} className={selectedSizes.includes(siz) ? 'active' : ''} onClick={() => setSelectedSizes(prev => prev.includes(siz) ? prev.filter(x=>x!==siz) : [...prev,siz])}>{siz}</button>
                  ))}
                </div>
              </div>
            )}
            <div className="control-group price-filter">
              <label>价格区间</label>
              <div className="price-inputs">
                <input type="number" min="0" placeholder="最小" value={minPrice} onChange={(e) => setMinPrice(e.target.value.replace(/[^0-9]/g, ''))} />
                <span>-</span>
                <input type="number" min="0" placeholder="最大" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value.replace(/[^0-9]/g, ''))} />
              </div>
            </div>
            <div className="control-group view-toggle">
              <label>视图</label>
              <div className="view-options">
                <button type="button" className={viewMode === 'grid' ? 'active' : ''} onClick={() => setViewMode('grid')}>网格</button>
                <button type="button" className={viewMode === 'list' ? 'active' : ''} onClick={() => setViewMode('list')}>列表</button>
              </div>
            </div>
            <div className="control-group pagination-controls">
              <label>分页</label>
              <div className="pagination-inner">
                <button type="button" onClick={goPrev} disabled={currentPage === 1}>上一页</button>
                <span className="page-info">{currentPage} / {totalPages}</span>
                <button type="button" onClick={goNext} disabled={currentPage === totalPages}>下一页</button>
                <select value={pageSize} onChange={(e) => onChangePageSize(Number(e.target.value))}>
                  <option value={12}>12</option>
                  <option value={20}>20</option>
                  <option value={40}>40</option>
                </select>
              </div>
            </div>
          </div>

          {selectedKeys.length > 0 && (
            <div className="compare-table">
              <div className="table-header">已选对比（{selectedKeys.length}）</div>
              <div className="table-body">
                <table>
                  <thead>
                    <tr>
                      <th>名称</th>
                      <th>价格</th>
                      <th>平台</th>
                      <th>店铺</th>
                    </tr>
                  </thead>
                  <tbody>
                    {sortedProducts.filter(p => selectedKeys.includes(getSelectKey(p))).map((p, i) => (
                      <tr key={i}>
                        <td>{p.goodsName}</td>
                        <td>¥{p.goodsPrice.toLocaleString()}</td>
                        <td>{getPlatformName(p.mallType)}</td>
                        <td>{getSellerFromLink(p.goodsLink)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          <div className="compare-group">
            <div className={`product-grid ${viewMode === 'list' ? 'list' : ''}`}>
              {currentPageProducts.map((product, productIndex) => (
                <div 
                    key={productIndex} 
                    className={`product-card ${viewMode === 'list' ? 'list' : ''}`}
                >
                  <div className="select-row">
                    <label className="select-checkbox">
                      <input type="checkbox" checked={isSelected(getSelectKey(product))} onChange={() => toggleSelect(product)} />
                      <span>对比</span>
                    </label>
                    <span className="seller-name">{getSellerFromLink(product.goodsLink)}</span>
                  </div>

                  <div className="product-image-container">
                    <img 
                      src={product.imgUrl || 'https://via.placeholder.com/200x200?text=No+Image'} 
                        alt={product.goodsName} 
                        onError={(e) => {
                          (e.target as HTMLImageElement).src = 'https://via.placeholder.com/200x200?text=Image+Error';
                        }}
                      />
                      <div className={getPlatformBadgeClass(product.mallType)}>
                        {getPlatformName(product.mallType)}
                      </div>
                    </div>

                    <div className="product-card-content">
                      <h4 title={product.goodsName}>{product.goodsName}</h4>
                      {(function(){
                        const f = parseFacets(product.goodsName);
                        const parts = [f.brand, f.series, f.model, f.storage, f.color, f.size].filter(Boolean);
                        return parts.length ? (<div className="spec-line">{parts.join(' / ')}</div>) : null;
                      })()}

                      <div className="price-section">
                        <span className="currency">¥</span>
                        <span className="amount">{product.goodsPrice.toLocaleString()}</span>
                      </div>

                      <div style={{display:'flex', gap:8}}>
                        <a 
                          href={product.goodsLink} 
                          target="_blank" 
                          rel="noopener noreferrer" 
                          className={`shop-button ${product.mallType === 10 ? 'btn-rakuten' : product.mallType === 20 ? 'btn-yahoo' : ''}`}
                        >
                          立即购买
                        </a>
                        <button type="button" className="trend-button" onClick={() => {
                          const title = product.goodsName;
                          const labels = Array.from({length:12}, (_,i)=>{
                            const d = new Date();
                            d.setMonth(d.getMonth() - (11 - i));
                            const m = (d.getMonth()+1).toString().padStart(2,'0');
                            return `${d.getFullYear()}-${m}`;
                          });
                          const base = Math.max(1, product.goodsPrice);
                          let val = base * 1.0;
                          const series = labels.map((_,i)=>{
                            const drift = 1 + Math.sin(i/3) * 0.05;
                            const noise = 1 + ((Math.random() - 0.5) * 0.08);
                            val = Math.max(1, val * drift * noise);
                            return Math.round(val);
                          });
                          setTrendTitle(title);
                          setTrendLabels(labels);
                          setTrendSeries(series);
                          setTrendOpen(true);
                        }}>价格走势图</button>
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          </div>
          {trendOpen && (
            <div className="price-trend-modal" onClick={() => setTrendOpen(false)}>
              <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                  <span className="modal-title">{trendTitle}</span>
                  <button type="button" className="modal-close" onClick={() => setTrendOpen(false)}>×</button>
                </div>
                <div className="modal-body">
                  <div className="trend-chart">
                    {(function(){
                      const width = 600;
                      const height = 280;
                      const padding = 32;
                      const xs = trendSeries.map((_,i)=> padding + (i*(width - 2*padding))/(trendSeries.length-1));
                      const minV = Math.min(...trendSeries);
                      const maxV = Math.max(...trendSeries);
                      const ys = trendSeries.map(v => height - padding - ((v - minV) / (Math.max(1, maxV - minV))) * (height - 2*padding));
                      const path = xs.map((x,i)=> `${i===0?'M':'L'}${x},${ys[i]}`).join(' ');
                      const gridY = [0.25,0.5,0.75].map(r => height - padding - r*(height - 2*padding));
                      return (
                        <svg width={width} height={height} role="img" aria-label="价格趋势图">
                          {gridY.map((gy,i)=> (
                            <line key={i} x1={padding} y1={gy} x2={width-padding} y2={gy} stroke="#eee" />
                          ))}
                          <path d={path} fill="none" stroke="#ff4400" strokeWidth={2} />
                          {xs.map((x,i)=> (
                            <circle key={i} cx={x} cy={ys[i]} r={3} fill="#ff4400" />
                          ))}
                          <text x={padding} y={height-8} fontSize={12} fill="#999">{trendLabels[0]}</text>
                          <text x={width-padding-60} y={height-8} fontSize={12} fill="#999">{trendLabels[trendLabels.length-1]}</text>
                        </svg>
                      );
                    })()}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {!loading && !error && compareGroups.length === 0 && query && (
        <div className="no-results">関連商品が見つかりません</div>
      )}
    </div>
  );
};

export default ComparePage;
