import React, { useState } from 'react';
import UserSidebar from '../components/UserSidebar';
import './BrowseHistoryPage.css';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  browseTime: string;
}

const BrowseHistoryPage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([
    {
      id: 1,
      name: 'iPhone 15 Pro Max 256GB',
      price: 9999,
      imageUrl: 'https://picsum.photos/100/100?random=1',
      platform: 'タオバオ',
      browseTime: '2023-12-24 10:30'
    },
    {
      id: 2,
      name: 'MacBook Air M2 13インチ',
      price: 8499,
      imageUrl: 'https://picsum.photos/100/100?random=2',
      platform: '京東',
      browseTime: '2023-12-23 15:45'
    },
    {
      id: 3,
      name: 'AirPods Pro 第2世代',
      price: 1899,
      imageUrl: 'https://picsum.photos/100/100?random=3',
      platform: '天猫',
      browseTime: '2023-12-22 09:20'
    },
    {
      id: 4,
      name: 'iPad Air 10.9インチ',
      price: 4399,
      imageUrl: 'https://picsum.photos/100/100?random=4',
      platform: '拼多多',
      browseTime: '2023-12-21 14:10'
    }
  ]);

  const clearHistory = () => {
    setProducts([]);
  };

  return (
    <div className="browse-history-page-wrapper" style={{ display: 'flex', maxWidth: '1200px', margin: '20px auto', gap: '20px', padding: '0 15px' }}>
      <UserSidebar />
      <div className="browse-history-page" style={{ flex: 1, padding: '20px', margin: 0, maxWidth: 'none', background: '#fff' }}>
      <div className="header">
        <h2>閲覧履歴</h2>
        {products.length > 0 && (
          <button className="clear-button" onClick={clearHistory}>
            履歴をクリア
          </button>
        )}
      </div>

      {products.length === 0 ? (
        <div className="no-products">閲覧履歴がありません</div>
      ) : (
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-item">
              <div className="product-info">
                <div className="product-image">
                  <img 
                    src={product.imageUrl} 
                    alt={product.name} 
                  />
                </div>
                <div className="product-details">
                  <h4>{product.name}</h4>
                  <div className="price">¥{product.price}</div>
                  <div className="platform">{product.platform}</div>
                  <div className="browse-time">{product.browseTime}</div>
                </div>
              </div>
              <div className="product-actions">
                <button className="buy-button">購入する</button>
              </div>
            </div>
          ))}
        </div>
      )}
      </div>
    </div>
  );
};

export default BrowseHistoryPage;
