import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './Header.css';

const Header: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  // 检查当前是否在比价页面
  const isComparePage = location.pathname === '/compare';

  // 检查用户登录状态
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      // 通常这里会调用API获取用户信息
      // 模拟获取用户名
      try {
        // 解析JWT token获取用户名（如果有的话）
        const tokenData = JSON.parse(atob(token.split('.')[1]));
        setUsername(tokenData.username || 'ユーザー');
      } catch (e) {
        setUsername('ユーザー');
      }
    } else {
      setIsLoggedIn(false);
      setUsername('');
    }
  }, []);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData(e.target as HTMLFormElement);
    const query = formData.get('query')?.toString();
    if (query) {
      // 可以跳转到比价页面并传递搜索参数
      navigate(`/compare?query=${encodeURIComponent(query)}`);
    }
  };

  return (
    <header className="header">
      <div className="header-top">
        <div className="logo">
          <Link to="/">惠購価格比較</Link>
        </div>
        <nav className="nav">
          <Link to="/">ホーム</Link>
          <Link to="/compare">価格比較</Link>
          <Link to="/profile">マイページ</Link>
        </nav>
        <div className="user-actions">
          {isLoggedIn ? (
            <>
              <Link to="/profile">{username}</Link>
              <button 
                onClick={() => {
                  localStorage.removeItem('token');
                  setIsLoggedIn(false);
                  navigate('/');
                }}
                className="logout-button"
              >
                ログアウト
              </button>
            </>
          ) : (
            <>
              <Link to="/login">ログイン</Link>
              <Link to="/register">登録</Link>
            </>
          )}
        </div>
      </div>
      {!isComparePage && (
        <div className="header-search">
          <form onSubmit={handleSearch}>
            <input 
              type="text" 
              name="query" 
              placeholder="商品を検索..." 
              className="search-input"
            />
            <button type="submit" className="search-button">検索</button>
          </form>
        </div>
      )}
    </header>
  );
};

export default Header;
