import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';
import { goodsApi } from '../services/api';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  originalPrice?: number;
}

const Home: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();
  const categories = [
    { key: 'smartphone', name: 'スマートフォン', image: 'https://source.unsplash.com/80x80/?smartphone', query: 'スマートフォン' },
    { key: 'laptop', name: 'パソコン', image: 'https://source.unsplash.com/80x80/?laptop', query: 'パソコン' },
    { key: 'appliance', name: '家電', image: 'https://source.unsplash.com/80x80/?appliance', query: '家電' },
    { key: 'fashion', name: 'ファッション', image: 'https://source.unsplash.com/80x80/?fashion', query: 'ファッション' },
    { key: 'beauty', name: '美容', image: 'https://source.unsplash.com/80x80/?beauty', query: '美容' },
    { key: 'book', name: '本', image: 'https://source.unsplash.com/80x80/?books', query: '本' }
  ];
  const hotTags = ['iPhone 15', 'MacBook', 'AirPods', 'Nintendo Switch', 'Galaxy S24', 'Sony WH-1000XM5'];

  // 实际API调用 - 获取推荐商品
  useEffect(() => {
    fetchRecommendedProducts();
  }, []);

  const fetchRecommendedProducts = async () => {
    setLoading(true);
    try {
      // 使用api.ts中定义的API服务
      const response = await goodsApi.getAllGoods(1, 6);

      console.log('API Response:', response.data); // デバッグ用ログ

      // 转换后端数据到前端Product类型
      let records = [];
      if (response.data && response.data.data && response.data.data.records) {
        records = response.data.data.records;
      } else {
        records = [];
      }

      console.log('Records:', records); // デバッグ用ログ

      const productsData = records.map((item: any) => ({
        id: item.goodsId,
        name: item.goodsName,
        price: item.goodsPrice,
        imageUrl: typeof item.imgUrl === 'string' ? item.imgUrl : '',
        platform: getPlatformName(item.mallType),
        originalPrice: item.goodsPrice ? item.goodsPrice * 1.1 : undefined
      }));

      console.log('Products Data:', productsData); // デバッグ用ログ

      setProducts(productsData);
    } catch (err) {
      console.error('获取推荐商品失败:', err);
      // 如果API失败，使用默认数据
      setProducts([
        {
          id: 1,
          name: '暂无商品数据',
          price: 0,
          imageUrl: 'https://picsum.photos/200/200?random=1',
          platform: '暂无',
          originalPrice: 0
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/compare?query=${encodeURIComponent(searchQuery)}`);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'ヤフー';
      case 30: return 'タオバオ';
      default: return '不明なプラットフォーム';
    }
  };

  return (
    <div className="home">
      <div className="hero">
        <h1 className="hero-title">惠购价格比较</h1>
        <p className="hero-subtitle">一站式比价，快速找到更省的购买渠道</p>
        <form onSubmit={handleSearch} className="hero-search">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="搜索商品名称进行比价"
            className="hero-input"
          />
          <button type="submit" className="hero-button">开始比价</button>
        </form>
        <div className="tag-list">
          {hotTags.map(tag => (
            <button key={tag} className="tag-chip" onClick={() => navigate(`/compare?query=${encodeURIComponent(tag)}`)}>{tag}</button>
          ))}
        </div>
      </div>

      <div className="categories">
        <h2>热门分类</h2>
        <div className="category-list">
          {categories.map((c) => (
            <div 
              key={c.key} 
              className="category-item"
              onClick={() => navigate(`/compare?query=${encodeURIComponent(c.name)}`)}
              style={{ cursor: 'pointer' }}
            >
              <img 
                src={(function(){
                  const url = (c.image || '').trim();
                  if (!url) return 'https://picsum.photos/80/80?random=category';
                  if (url.startsWith('//')) return 'https:' + url;
                  if (!/^https?:\/\//i.test(url)) return 'https://picsum.photos/80/80?random=category';
                  return url.replace(/^http:\/\//i, 'https://');
                })()}
                alt={c.name}
                loading="lazy"
                decoding="async"
                referrerPolicy="no-referrer"
                onError={(e) => {
                  const img = e.target as HTMLImageElement;
                  img.onerror = null;
                  img.src = 'https://picsum.photos/80/80?random=category';
                }}
              />
              <span>{c.name}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="featured-products">
        <h2>热门比价推荐</h2>
        {loading ? (
          <div className="loading">読み込み中...</div>
        ) : (
          <div className="product-grid">
            {products.map((product) => (
              <Link to={`/compare?query=${encodeURIComponent(product.name)}`} key={product.id || Math.random()} className="product-card">
                <img 
                  src={(function(){
                    const url = (product.imageUrl || '').trim();
                    if (!url) return 'https://via.placeholder.com/400x200?text=No+Image';
                    if (url.startsWith('//')) return 'https:' + url;
                    if (!/^https?:\/\//i.test(url)) return 'https://via.placeholder.com/400x200?text=No+Image';
                    return url.replace(/^http:\/\//i, 'https://');
                  })()}
                  alt={product.name}
                  className="product-image"
                  loading="lazy"
                  decoding="async"
                  referrerPolicy="no-referrer"
                  onError={(e) => {
                    const img = e.target as HTMLImageElement;
                    img.onerror = null;
                    img.src = 'https://via.placeholder.com/400x200?text=Image+Error';
                  }}
                />
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <div className="product-price">
                    <span className="current-price">¥{product.price}</span>
                    {product.originalPrice && (
                      <span className="original-price">¥{product.originalPrice}</span>
                    )}
                  </div>
                  <div className="platform">{product.platform}</div>
                  {product.originalPrice && (
                    <div className="savings">
                      ¥{product.originalPrice - product.price}お得
                    </div>
                  )}
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
