import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  originalPrice?: number;
}

const Home: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);

  // 实际API调用 - 获取推荐商品
  useEffect(() => {
    fetchRecommendedProducts();
  }, []);

  const fetchRecommendedProducts = async () => {
    setLoading(true);
    try {
      // 获取最新或热门商品作为推荐
      const response = await axios.post('http://localhost:9090/goods/pageAll', {
        pageNum: 1,
        pageSize: 6
      });

      console.log('API Response:', response.data); // デバッグ用ログ

      // 转换后端数据到前端Product类型
      let records = [];
      if (response.data && response.data.data && response.data.data.records) {
        records = response.data.data.records;
      } else {
        records = [];
      }

      console.log('Records:', records); // デバッグ用ログ

      const productsData = records.map((item: any) => ({
        id: item.goodsId,
        name: item.goodsName,
        price: item.goodsPrice,
        imageUrl: item.imgUrl,
        platform: getPlatformName(item.mallType),
        originalPrice: item.goodsPrice ? item.goodsPrice * 1.1 : undefined // 假设原价是当前价格的1.1倍
      }));

      console.log('Products Data:', productsData); // デバッグ用ログ

      setProducts(productsData);
    } catch (err) {
      console.error('获取推荐商品失败:', err);
      // 如果API失败，使用默认数据
      setProducts([
        {
          id: 1,
          name: '暂无商品数据',
          price: 0,
          imageUrl: 'https://picsum.photos/200/200?random=1',
          platform: '暂无',
          originalPrice: 0
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '乐天';
      case 20: return '雅虎';
      case 30: return '淘宝';
      default: return '未知平台';
    }
  };

  return (
    <div className="home">
      <div className="banner">
        <img 
          src="https://picsum.photos/1200/300?random=banner" 
          alt="Banner" 
          className="banner-image"
        />
      </div>

      <div className="categories">
        <h2>热门分类</h2>
        <div className="category-list">
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=phone" alt="手机" />
            <span>手机</span>
          </div>
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=2" alt="电脑" />
            <span>电脑</span>
          </div>
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=3" alt="家电" />
            <span>家电</span>
          </div>
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=clothing" alt="服饰" />
            <span>服饰</span>
          </div>
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=beauty" alt="美妆" />
            <span>美妆</span>
          </div>
          <div className="category-item">
            <img src="https://picsum.photos/80/80?random=6" alt="图书" />
            <span>图书</span>
          </div>
        </div>
      </div>

      <div className="featured-products">
        <h2>推荐商品</h2>
        {loading ? (
          <div className="loading">加载中...</div>
        ) : (
          <div className="product-grid">
            {products.map((product) => (
              <Link to={`/compare?query=${encodeURIComponent(product.name)}`} key={product.id} className="product-card">
                <img src={product.imageUrl} alt={product.name} className="product-image" />
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <div className="product-price">
                    <span className="current-price">¥{product.price}</span>
                    {product.originalPrice && (
                      <span className="original-price">¥{product.originalPrice}</span>
                    )}
                  </div>
                  <div className="platform">{product.platform}</div>
                  {product.originalPrice && (
                    <div className="savings">
                      省¥{product.originalPrice - product.price}
                    </div>
                  )}
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
