import React, { useState, useEffect } from 'react';
import { goodsApi } from '../services/api';
import './MyCollectionPage.css';

interface Product {
  goodsId?: number;
  goodsName: string;
  goodsPrice: number;
  goodsLink: string;
  imgUrl?: string;
  mallType: number;
}

const MyCollectionPage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadCollections();
  }, []);

  const loadCollections = async () => {
    try {
      setLoading(true);
      const response = await goodsApi.getMyCollect(1, 20);
      setProducts(response.data.data?.records || []);
    } catch (err) {
      setError('获取收藏商品失败');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '乐天';
      case 20: return '雅虎';
      case 30: return '淘宝';
      default: return '未知平台';
    }
  };

  const handleCancelCollect = async (goodsId: number) => {
    // 在实际应用中，这里应该调用取消收藏的API
    console.log(`取消收藏商品: ${goodsId}`);
    // 更新本地状态
    setProducts(products.filter(product => product.goodsId !== goodsId));
  };

  if (loading) {
    return <div className="loading">加载中...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="my-collection-page">
      <h2>我的收藏</h2>
      {products.length === 0 ? (
        <div className="no-products">暂无收藏商品</div>
      ) : (
        <div className="product-list">
          {products.map((product) => (
            <div key={product.goodsId} className="product-item">
              <div className="product-info">
                <div className="product-image">
                  <img 
                    src={product.imgUrl || 'https://via.placeholder.com/100x100.png?text=产品'} 
                    alt={product.goodsName} 
                  />
                </div>
                <div className="product-details">
                  <h4>{product.goodsName}</h4>
                  <div className="price">¥{product.goodsPrice}</div>
                  <div className="platform">{getPlatformName(product.mallType)}</div>
                </div>
              </div>
              <div className="product-actions">
                <a 
                  href={product.goodsLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="buy-button"
                >
                  去购买
                </a>
                <button 
                  className="unlike-button"
                  onClick={() => handleCancelCollect(product.goodsId!)}
                >
                  取消收藏
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyCollectionPage;
