import React, { useState, useEffect } from 'react';
import { goodsApi, userApi, disclosureApi, notificationApi } from '../services/api';
import { useNavigate } from 'react-router-dom';
import UserSidebar from '../components/UserSidebar';
import websocketService, { useWebSocket } from '../services/websocketService';
import './ProfilePage.css';

const ProfilePage: React.FC = () => {
  const navigate = useNavigate();

  const isAdmin = (() => {
    const token = localStorage.getItem('token');
    if (!token) return false;
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload?.userId === 1;
    } catch {
      return false;
    }
  })();
  
  const [profile, setProfile] = useState<{ id: number; name: string; nickname?: string; avatar?: string; gender?: number; age?: number; birthDate?: string } | null>(null);
  const [errorMsg, setErrorMsg] = useState<string | null>(null);
  const [isEditing, setIsEditing] = useState<boolean>(false); // æ§åˆ¶ç¼–è¾‘çŠ¶æ€
  const [editForm, setEditForm] = useState({
    name: '',
    nickname: '',
    gender: 0,
    age: 0,
    birthDate: ''
  });
  
  // WebSocketç›¸å…³çŠ¶æ€
  const [wsStatus, setWsStatus] = useState<string>('disconnected');
  const [wsMessages, setWsMessages] = useState<any[]>([]);
  const [notifications, setNotifications] = useState<any[]>([]);
  
  // ä½¿ç”¨WebSocket Hook
  useEffect(() => {
    // åˆå§‹åŒ–WebSocketè¿æ¥ï¼Œå³ä½¿æ²¡æœ‰profile.idä¹Ÿå»ºç«‹åŸºç¡€è¿æ¥
    const wsCleanup = useWebSocket({
      onMessage: (message) => {
        console.log('Received WebSocket message:', message);
        setWsMessages(prev => [...prev, message]);
      },
      onOpen: () => {
        console.log('WebSocket connection opened');
        setWsStatus(websocketService.getStatus());
      },
      onClose: (event) => {
        console.log('WebSocket connection closed:', event);
        setWsStatus('disconnected');
      },
      onError: (error) => {
        console.error('WebSocket error:', error);
        setWsStatus('error');
      },
      onNotification: (notification) => {
        console.log('Received notification via WebSocket:', notification);
        setNotifications(prev => [notification, ...prev]);
        
        // æ˜¾ç¤ºæµè§ˆå™¨é€šçŸ¥
        if ('Notification' in window && Notification.permission === 'granted') {
          new Notification('æ–°ã—ã„é€šçŸ¥', {
            body: notification.message || 'é€šçŸ¥ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸',
            icon: '/favicon.ico'
          });
        }
      }
    });
    
    return wsCleanup;
  }, []); // ä»…åœ¨ç»„ä»¶æŒ‚è½½æ—¶è¿æ¥WebSocket
  
  // æ ¹æ®ç”¨æˆ·IDè¿æ¥åˆ°ç‰¹å®šç”¨æˆ·é¢‘é“
  useEffect(() => {
    if (profile?.id && websocketService) {
      // è¿æ¥åˆ°ç‰¹å®šç”¨æˆ·é¢‘é“
      websocketService.connect(profile.id);
    }
  }, [profile?.id]); // å½“profile.idå˜åŒ–æ—¶é‡æ–°è¿æ¥
  
  const [featureCounts, setFeatureCounts] = useState([
    { id: 1, name: 'ãƒã‚¤ã‚³ãƒ¬ã‚¯ã‚·ãƒ§ãƒ³', icon: 'â¤ï¸', count: 0 },
    { id: 2, name: 'é–²è¦§å±¥æ­´', icon: 'ğŸ•’', count: 0 },
    { id: 4, name: 'ãƒã‚¤ãƒ’ãƒ³ãƒˆ', icon: 'ğŸ“¢', count: 0 },
    { id: 5, name: 'é€šçŸ¥', icon: 'ğŸ””', count: 0 }
  ]);
  
  // ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ‡ãƒ¼ã‚¿ã®èª­ã¿è¾¼ã¿
  useEffect(() => {
