            </button>
          </div>
          
          {wsMessages.length > 0 && (
            <div style={{ marginTop: '10px' }}>
              <h4>æœ€è¿‘ã®ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸:</h4>
              <div style={{ maxHeight: '100px', overflowY: 'auto', fontSize: '12px', backgroundColor: 'white', padding: '5px' }}>
                {wsMessages.slice(-5).map((msg, index) => (
                  <div key={index}>{JSON.stringify(msg)}</div>
                ))}
              </div>
            </div>
          )}
          
          {notifications.length > 0 && (
            <div style={{ marginTop: '10px' }}>
              <h4>ãƒªã‚¢ãƒ«ã‚¿ã‚¤ãƒ é€šçŸ¥:</h4>
              <div style={{ maxHeight: '150px', overflowY: 'auto', fontSize: '12px', backgroundColor: 'white', padding: '5px', border: '1px solid #ccc' }}>
                {notifications.slice(0, 5).map((notification, index) => (
                  <div key={index} style={{ padding: '5px', borderBottom: '1px solid #eee' }}>
                    <div><strong>ã‚¿ã‚¤ãƒ—:</strong> {notification.type}</div>
                    <div><strong>ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸:</strong> {notification.message}</div>
                  </div>
