import React, { useState } from 'react';
import './BrowseHistoryPage.css';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  browseTime: string;
}

const BrowseHistoryPage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([
    {
      id: 1,
      name: 'iPhone 15 Pro Max 256GB',
      price: 9999,
      imageUrl: 'https://via.placeholder.com/100x100.png?text=iPhone',
      platform: '淘宝',
      browseTime: '2023-12-24 10:30'
    },
    {
      id: 2,
      name: 'MacBook Air M2 13英寸',
      price: 8499,
      imageUrl: 'https://via.placeholder.com/100x100.png?text=MacBook',
      platform: '京东',
      browseTime: '2023-12-23 15:45'
    },
    {
      id: 3,
      name: 'AirPods Pro 第二代',
      price: 1899,
      imageUrl: 'https://via.placeholder.com/100x100.png?text=AirPods',
      platform: '天猫',
      browseTime: '2023-12-22 09:20'
    },
    {
      id: 4,
      name: 'iPad Air 10.9英寸',
      price: 4399,
      imageUrl: 'https://via.placeholder.com/100x100.png?text=iPad',
      platform: '拼多多',
      browseTime: '2023-12-21 14:10'
    }
  ]);

  const clearHistory = () => {
    setProducts([]);
  };

  return (
    <div className="browse-history-page">
      <div className="header">
        <h2>浏览历史</h2>
        {products.length > 0 && (
          <button className="clear-button" onClick={clearHistory}>
            清空历史
          </button>
        )}
      </div>

      {products.length === 0 ? (
        <div className="no-products">暂无浏览历史</div>
      ) : (
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-item">
              <div className="product-info">
                <div className="product-image">
                  <img 
                    src={product.imageUrl} 
                    alt={product.name} 
                  />
                </div>
                <div className="product-details">
                  <h4>{product.name}</h4>
                  <div className="price">¥{product.price}</div>
                  <div className="platform">{product.platform}</div>
                  <div className="browse-time">{product.browseTime}</div>
                </div>
              </div>
              <div className="product-actions">
                <button className="buy-button">去购买</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BrowseHistoryPage;
