import React, { useState } from 'react';
import './ComparisonHistoryPage.css';

interface HistoryItem {
  id: number;
  query: string;
  time: string;
  resultCount: number;
}

const ComparisonHistoryPage: React.FC = () => {
  const [history, setHistory] = useState<HistoryItem[]>([
    {
      id: 1,
      query: 'iPhone 15',
      time: '2023-12-24 10:30',
      resultCount: 12
    },
    {
      id: 2,
      query: 'MacBook Air',
      time: '2023-12-23 15:45',
      resultCount: 8
    },
    {
      id: 3,
      query: 'AirPods Pro',
      time: '2023-12-22 09:20',
      resultCount: 5
    },
    {
      id: 4,
      query: 'iPad Air',
      time: '2023-12-21 14:10',
      resultCount: 7
    }
  ]);

  const clearHistory = () => {
    setHistory([]);
  };

  const searchAgain = (query: string) => {
    console.log(`重新搜索: ${query}`);
    // 在实际应用中，这里应该导航到比价页面并传递搜索词
  };

  return (
    <div className="comparison-history-page">
      <div className="header">
        <h2>比价历史</h2>
        {history.length > 0 && (
          <button className="clear-button" onClick={clearHistory}>
            清空历史
          </button>
        )}
      </div>

      {history.length === 0 ? (
        <div className="no-history">暂无比价历史</div>
      ) : (
        <div className="history-list">
          {history.map((item) => (
            <div key={item.id} className="history-item">
              <div className="history-info">
                <div className="query">{item.query}</div>
                <div className="details">
                  <span className="time">{item.time}</span>
                  <span className="result-count">{item.resultCount}个结果</span>
                </div>
              </div>
              <div className="history-actions">
                <button 
                  className="search-again-button"
                  onClick={() => searchAgain(item.query)}
                >
                  重新搜索
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ComparisonHistoryPage;
