import React, { useState, useEffect } from 'react';
import { goodsApi, userApi } from '../services/api';
import { useNavigate } from 'react-router-dom';
import UserSidebar from '../components/UserSidebar';
import './ProfilePage.css';

const ProfilePage: React.FC = () => {
  const navigate = useNavigate();

  const isAdmin = (() => {
    const token = localStorage.getItem('token');
    if (!token) return false;
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload?.userId === 1;
    } catch {
      return false;
    }
  })();

  const [userInfo, setUserInfo] = useState({
    name: '„É¶„Éº„Ç∂„Éº135****8888',
    avatar: 'https://picsum.photos/80/80?random=avatar',
    level: 'VIP 3',
    points: 1250,
    balance: 86.5
  });

  const [featureCounts, setFeatureCounts] = useState([
    { id: 1, name: '„Éû„Ç§„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥', icon: '‚ù§Ô∏è', count: 0 },
    { id: 2, name: 'Èñ≤Ë¶ßÂ±•Ê≠¥', icon: 'üïí', count: 0 },
    { id: 4, name: '„Éû„Ç§„Éí„É≥„Éà', icon: 'üì¢', count: 0 }
  ]);

  // „É¶„Éº„Ç∂„Éº„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø
  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥ÂïÜÂìÅÊï∞„ÅÆÂèñÂæó
      const collectResponse = await goodsApi.getMyCollect(1, 100); // „Åô„Åπ„Å¶„ÅÆ„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥„ÇíÂèñÂæó
      const collectCount = collectResponse.data.data?.records?.length || 0;

      // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Êï∞„ÅÆÊõ¥Êñ∞
      setFeatureCounts(prev => 
        prev.map(item => 
          item.id === 1 ? { ...item, count: collectCount } : item
        )
      );

      // Èñ≤Ë¶ßÂ±•Ê≠¥„ÅÆÂèñÂæó
      const historyResponse = await userApi.getHistory(1, 100);
      const historyCount = historyResponse.data.data?.length || 0;

      // „Éí„É≥„Éà„Éá„Éº„Çø„ÅÆ„Ç∑„Éü„É•„É¨„Éº„ÉàÔºà„Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Å´API„Åå„Å™„ÅÑ„Åü„ÇÅÔºâ
      // ÂÆüÈöõ„ÅÆ„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥„Åß„ÅØ„ÄÅ„Åì„Åì„Å´Ë©≤ÂΩì„Åô„ÇãAPI„ÇíÂëº„Å≥Âá∫„ÅôÂøÖË¶Å„Åå„ÅÇ„Çä„Åæ„Åô
      const tipCount = 3; // „Éí„É≥„ÉàÊï∞„ÅÆ„Ç∑„Éü„É•„É¨„Éº„Éà

      setFeatureCounts(prev => 
        prev.map(item => {
          if (item.id === 2) return { ...item, count: historyCount };
          if (item.id === 4) return { ...item, count: tipCount };
          return item;
        })
      );
    } catch (error) {
      console.error('„É¶„Éº„Ç∂„Éº„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó:', error);
    }
  };

  const handleFeatureClick = (id: number) => {
    switch(id) {
      case 1:
        navigate('/my-collection');
        break;
      case 2:
        navigate('/browse-history');
        break;
      case 4:
        navigate('/my-tip');
        break;
      default:
        break;
    }
  };

  const orders = [
    { id: 1, name: 'ÂæÖÂèóÂèñ', icon: 'üì¶', count: 1 }
  ];

  return (
    <div className="profile-page-wrapper" style={{ display: 'flex', maxWidth: '1200px', margin: '20px auto', gap: '20px', padding: '0 15px' }}>
      <UserSidebar />
      <div className="profile-page" style={{ flex: 1, padding: 0, margin: 0, maxWidth: 'none', minHeight: 'auto' }}>
      <div className="profile-header">
        <div className="avatar-section">
          <img src={userInfo.avatar} alt="„Ç¢„Éê„Çø„Éº" className="avatar" />
          <div className="user-info">
            <h2>{userInfo.name}</h2>
            <div className="user-level">{userInfo.level}</div>
          </div>
        </div>
        <div className="user-stats">
          <div className="stat-item">
            <div className="stat-value">¬•{userInfo.balance}</div>
            <div className="stat-label">ÊÆãÈ´ò</div>
          </div>
          <div className="stat-item">
            <div className="stat-value">{userInfo.points}</div>
            <div className="stat-label">„Éù„Ç§„É≥„Éà</div>
          </div>
        </div>
      </div>

      <div className="profile-features">
        {featureCounts.map(feature => (
          <div 
            key={feature.id} 
            className="feature-item"
            onClick={() => handleFeatureClick(feature.id)}
            style={{ cursor: 'pointer' }}
          >
            <div className="feature-icon">{feature.icon}</div>
            <div className="feature-name">{feature.name}</div>
            {feature.count > 0 && <div className="feature-count">{feature.count}</div>}
          </div>
        ))}
      </div>

      <div className="settings-section">
        <div className="setting-item" onClick={() => navigate('/comparison-history')} style={{ cursor: 'pointer' }}>
          <span>‰æ°Ê†ºÊØîËºÉÂ±•Ê≠¥</span>
          <span className="arrow">‚Ä∫</span>
        </div>
        {isAdmin && (
          <div className="setting-item" onClick={() => navigate('/admin/disclosures')} style={{ cursor: 'pointer' }}>
            <span>ÁÆ°ÁêÜËÄÖÔºöÊäïÁ®øÂØ©Êüª</span>
            <span className="arrow">‚Ä∫</span>
          </div>
        )}
        <div className="setting-item">
          <span>„É°„ÉÉ„Çª„Éº„Ç∏ÈÄöÁü•</span>
          <span className="arrow">‚Ä∫</span>
        </div>
        <div className="setting-item">
          <span>„Ç¢„Ç´„Ç¶„É≥„Éà„Çª„Ç≠„É•„É™„ÉÜ„Ç£</span>
          <span className="arrow">‚Ä∫</span>
        </div>
        <div className="setting-item">
          <span>„Ç´„Çπ„Çø„Éû„Éº„Çµ„Éù„Éº„Éà</span>
          <span className="arrow">‚Ä∫</span>
        </div>
        <div className="setting-item">
          <span>Ë®≠ÂÆö</span>
          <span className="arrow">‚Ä∫</span>
        </div>
      </div>

      <div className="logout-section">
        <button className="logout-button">„É≠„Ç∞„Ç¢„Ç¶„Éà</button>
      </div>
      </div>
    </div>
  );
};

export default ProfilePage;
