import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';
import './ComparePage.css';

interface Product {
  goodsId?: number;
  goodsName: string;
  goodsPrice: number;
  goodsLink: string;
  imgUrl?: string;
  mallType: number;
}

interface CompareGroup {
  goodsName: string;
  goodsList: Product[];
  lowestPrice: number | null;
  lowestPlatform?: string;
}

const ComparePage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [query, setQuery] = useState(searchParams.get('query') || '');
  const [compareGroups, setCompareGroups] = useState<CompareGroup[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // 実際のAPI呼び出し
  useEffect(() => {
    if (query.trim()) {
      searchProducts(query);
    }
  }, [query]);

  const searchProducts = async (searchQuery: string) => {
    setLoading(true);
    setError(null);

    try {
      console.log('Calling API: http://localhost:9090/goods/compare with query:', searchQuery); // デバッグ用ログ

      // 実際のAPI呼び出し
      const response = await axios.post('http://localhost:9090/goods/compare', {
        query: searchQuery
      });

      console.log('API Response:', response.data); // デバッグ用ログ

      let responseData = [];
      if (response.data && response.data.data) {
        responseData = Array.isArray(response.data.data) ? response.data.data : [];
      } else {
        responseData = [];
      }

      console.log('Raw Response Data:', responseData); // デバッグ用ログ

      console.log('Raw Response Data:', responseData); // デバッグ用ログ

      // レスポンスデータが商品比較グループ形式か、単一商品リスト形式かを判定
      let processedData = [];

      if (responseData.length > 0) {
        // 最初のアイテムを確認してデータ形式を判定
        const firstItem = responseData[0];

        console.log('First item keys:', Object.keys(firstItem)); // デバッグ用ログ
        console.log('First item content:', firstItem); // デバッグ用ログ

        if (firstItem.goodsList !== undefined) {
          // 商品比較グループ形式 (各グループがgoodsListを持つ)
          console.log('Detected group format'); // デバッグ用ログ
          processedData = responseData.map((group: any) => ({
            goodsName: group.goodsName || '不明な商品',
            goodsList: Array.isArray(group.goodsList) ? group.goodsList : [],
            lowestPrice: group.lowestPrice || null,
            lowestPlatform: group.lowestPlatform || '不明'
          }));
        } else {
          // APIが既にグループ化データを返している場合 - 再グループ化
          console.log('Detected list format - reprocessing grouped data'); // デバッグ用ログ
          console.log('Sample item:', responseData[0]); // デバッグ用ログ
          console.log('Sample item keys:', Object.keys(responseData[0])); // デバッグ用ログ
          console.log('Sample item values:', Object.values(responseData[0])); // デバッグ用ログ

          // APIが返すグループデータを適切に処理
          processedData = responseData.map((group: any) => {
            // グループ内の商品リストを処理
            const goodsList = Array.isArray(group.goodsList) ? group.goodsList : [];

            // 最低価格とプラットフォームを特定
            let lowestPrice = null;
            let lowestPlatform = '不明';

            if (goodsList.length > 0) {
              // 最初の商品から価格を取得（実際にはすべての商品を比較する必要がある）
              const firstProduct = goodsList[0];
              lowestPrice = group.lowestPrice || firstProduct.goodsPrice || firstProduct.price || firstProduct.currentPrice || firstProduct.salePrice || null;

              // プラットフォーム名を取得
              const platformType = firstProduct.mallType || firstProduct.platformType || firstProduct.shopType || 0;
              switch(platformType) {
                case 10:
                  lowestPlatform = '楽天';
                  break;
                case 20:
                  lowestPlatform = 'ヤフー';
                  break;
                case 30:
                  lowestPlatform = 'タオバオ';
                  break;
                default:
                  lowestPlatform = '不明なプラットフォーム';
              }
            } else {
              // 商品リストが空の場合、グループ情報から価格とプラットフォームを取得
              lowestPrice = group.lowestPrice || null;
              lowestPlatform = group.lowestPlatform || '不明';
            }

            return {
              goodsName: group.goodsName || '不明な商品',
              goodsList: goodsList,
              lowestPrice: lowestPrice,
              lowestPlatform: lowestPlatform
            };
          });

          console.log('Processed grouped data:', processedData); // デバッグ用ログ
        }
      }

      console.log('Final processed data:', processedData); // デバッグ用ログ

      setCompareGroups(processedData);
      setLoading(false);
    } catch (err) {
      setError('検索に失敗しました。後ほど再試行してください');
      setLoading(false);
      console.error(err);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (query.trim()) {
      searchProducts(query);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'ヤフー';
      case 30: return 'タオバオ';
      default: return '不明なプラットフォーム';
    }
  };

  const getPlatformColor = (mallType: number): string => {
    switch (mallType) {
      case 10: return '#ff4400'; // 楽天
      case 20: return '#00a0e9'; // ヤフー
      case 30: return '#ff6600'; // タオバオ
      default: return '#666';
    }
  };

  return (
    <div className="compare-page">
      <div className="search-section">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="商品名を入力して価格比較..."
            className="search-input"
          />
          <button type="submit" className="search-button">検索</button>
        </form>
      </div>

      {loading && <div className="loading">検索中...</div>}
      {error && <div className="error">{error}</div>}

      {!loading && !error && compareGroups.length > 0 && (
        <div className="compare-results">
          <h2>価格比較結果</h2>
          {compareGroups.map((group, index) => (
            <div key={index} className="compare-group">
              <div className="group-header">
                <h3>{group.goodsName}</h3>
                {group.lowestPrice !== null && (
                  <div className="lowest-price">
                    <span>最安値: </span>
                    <span className="price">¥{group.lowestPrice}</span>
                    <span className="platform">({group.lowestPlatform})</span>
                  </div>
                )}
              </div>

              <div className="product-list">
                {(group.goodsList || []).map((product, productIndex) => (
                  <div key={productIndex} className="product-item">
                    <div className="product-info">
                      <div className="product-image">
                        <img 
                          src={product.imgUrl || 'https://picsum.photos/100/100?random=product'} 
                          alt={product.goodsName} 
                        />
                      </div>
                      <div className="product-details">
                        <h4>{product.goodsName}</h4>
                        <div className="price" style={{ color: getPlatformColor(product.mallType) }}>
                          ¥{product.goodsPrice}
                        </div>
                        <div className="platform" style={{ color: getPlatformColor(product.mallType) }}>
                          {getPlatformName(product.mallType)}
                        </div>
                      </div>
                    </div>
                    <div className="product-actions">
                      <a 
                        href={product.goodsLink} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="buy-button"
                      >
                        購入する
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {!loading && !error && compareGroups.length === 0 && query && (
        <div className="no-results">関連商品が見つかりません</div>
      )}
    </div>
  );
};

export default ComparePage;
