import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserSidebar from '../components/UserSidebar';
import { disclosureApi } from '../services/api';

interface Disclosure {
  disclosureId: number;
  title: string;
  content: string;
  link: string;
  disclosurePrice: number;
  imgUrl?: string;
  createTime: string;
  status: number;
  author?: number;
}

const AdminDisclosurePage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [submittingId, setSubmittingId] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [items, setItems] = useState<Disclosure[]>([]);

  const isAdmin = (() => {
    const token = localStorage.getItem('token');
    if (!token) return false;
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload?.userId === 1;
    } catch {
      return false;
    }
  })();

  const load = async () => {
    try {
      setLoading(true);
      setError(null);
      const res = await disclosureApi.getPendingDisclosure(1, 200);
      setItems(res.data?.data || []);
    } catch (e: any) {
      setError(e?.response?.data?.msg || e?.response?.data?.message || '読み込みに失敗しました。');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login', { replace: true });
      return;
    }
    if (!isAdmin) {
      navigate('/profile', { replace: true });
      return;
    }
    load();
  }, [isAdmin, navigate]);

  const audit = async (disclosureId: number, status: 1 | 2) => {
    try {
      setSubmittingId(disclosureId);
      await disclosureApi.audit(disclosureId, status);
      await load();
    } catch (e: any) {
      setError(e?.response?.data?.msg || e?.response?.data?.message || '操作に失敗しました。');
    } finally {
      setSubmittingId(null);
    }
  };

  return (
    <div style={{ display: 'flex', maxWidth: '1200px', margin: '20px auto', gap: '20px', padding: '0 15px' }}>
      <UserSidebar />
      <div style={{ flex: 1, background: '#fff', padding: '20px', border: '1px solid #eee' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 }}>
          <h2 style={{ margin: 0 }}>管理者：投稿審査</h2>
          <div style={{ display: 'flex', gap: 10 }}>
            <button
              onClick={() => navigate('/community')}
              style={{ border: '1px solid #ddd', background: '#fff', padding: '8px 12px', cursor: 'pointer' }}
            >
              公開一覧へ
            </button>
            <button
              onClick={load}
              disabled={loading}
              style={{ border: '1px solid #ddd', background: '#fff', padding: '8px 12px', cursor: loading ? 'not-allowed' : 'pointer' }}
            >
              更新
            </button>
          </div>
        </div>

        {error && (
          <div style={{ background: '#fff2f0', color: '#cf1322', border: '1px solid #ffccc7', padding: 10, marginBottom: 12 }}>
            {error}
          </div>
        )}

        {loading ? (
          <div>読み込み中...</div>
        ) : items.length === 0 ? (
          <div style={{ color: '#666' }}>待审核の投稿はありません。</div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))', gap: 16 }}>
            {items.map((it) => (
              <div key={it.disclosureId} style={{ border: '1px solid #eee', borderRadius: 6, overflow: 'hidden' }}>
                {it.imgUrl ? (
                  <div style={{ height: 180, background: '#fafafa' }}>
                    <img
                      src={it.imgUrl}
                      alt={it.title}
                      style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                      onError={(e) => {
                        (e.target as HTMLImageElement).style.display = 'none';
                      }}
                    />
                  </div>
                ) : (
                  <div style={{ height: 180, background: '#fafafa' }} />
                )}

                <div style={{ padding: 12 }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', gap: 10, marginBottom: 6 }}>
                    <div style={{ fontWeight: 700, color: '#333', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
                      {it.title}
                    </div>
                    <div style={{ color: '#ff4400', fontWeight: 700 }}>¥{it.disclosurePrice}</div>
                  </div>

                  <div style={{ color: '#666', fontSize: 13, lineHeight: 1.5, marginBottom: 10 }}>
                    {it.content}
                  </div>

                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: 10 }}>
                    <a
                      href={it.link}
                      target="_blank"
                      rel="noreferrer"
                      style={{ color: '#1677ff', fontSize: 13, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', flex: 1 }}
                      title={it.link}
                    >
                      {it.link}
                    </a>
                    <div style={{ display: 'flex', gap: 8 }}>
                      <button
                        onClick={() => audit(it.disclosureId, 1)}
                        disabled={submittingId === it.disclosureId}
                        style={{ border: '1px solid #b7eb8f', background: '#f6ffed', padding: '6px 10px', cursor: submittingId === it.disclosureId ? 'not-allowed' : 'pointer' }}
                      >
                        承認
                      </button>
                      <button
                        onClick={() => audit(it.disclosureId, 2)}
                        disabled={submittingId === it.disclosureId}
                        style={{ border: '1px solid #ffccc7', background: '#fff2f0', padding: '6px 10px', cursor: submittingId === it.disclosureId ? 'not-allowed' : 'pointer' }}
                      >
                        却下
                      </button>
                    </div>
                  </div>

                  <div style={{ marginTop: 10, color: '#999', fontSize: 12, display: 'flex', justifyContent: 'space-between' }}>
                    <span>ID: {it.disclosureId}</span>
                    <span>{it.createTime}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDisclosurePage;
