  const loadUserData = async () => {
    try {
      // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥ÂïÜÂìÅÊï∞„ÅÆÂèñÂæó
      const collectResponse = await goodsApi.getMyCollect(1, 100); // „Åô„Åπ„Å¶„ÅÆ„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥„ÇíÂèñÂæó
      const collectCount = collectResponse.data.data?.records?.length || 0;

      // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Êï∞„ÅÆÊõ¥Êñ∞
      setFeatureCounts(prev => 
        prev.map(item => 
