import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import './ComparePage.css';
import { goodsApi } from '../services/api';

interface Product {
  goodsId?: number;
  goodsName: string;
  goodsPrice: number;
  goodsLink: string;
  imgUrl?: string;
  mallType: number;
}

interface CompareGroup {
  goodsName: string;
  goodsList: Product[];
  lowestPrice: number | null;
  lowestPlatform?: string;
}

const ComparePage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [query, setQuery] = useState(searchParams.get('query') || '');
  const [compareGroups, setCompareGroups] = useState<CompareGroup[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [sortOption, setSortOption] = useState('price_asc');
  const [selectedPlatforms, setSelectedPlatforms] = useState<number[]>([]);
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);
  const [selectedKeys, setSelectedKeys] = useState<string[]>([]);

  // 仅在页面加载时，如果有URL参数，则执行搜索
  useEffect(() => {
    if (query.trim()) {
      searchProducts(query);
    }
  }, []);

  const searchProducts = async (searchQuery: string) => {
    if (!searchQuery.trim()) return; // 如果查询为空则不执行搜索

    setLoading(true);
    setError(null);

    try {
      console.log('Calling API: goodsApi.compareGoods with query:', searchQuery); // デバッグ用ログ

      // 使用api.ts中定义的API服务
      const response = await goodsApi.compareGoods(searchQuery);

      console.log('API Response:', response.data); // デバッグ用ログ

      let responseData = [];
      if (response.data && response.data.data) {
        responseData = Array.isArray(response.data.data) ? response.data.data : [response.data.data];
      } else {
        responseData = [];
      }

      console.log('Raw Response Data:', responseData); // デバッグ用ログ

      // レスポンスデータを処理
      let processedData = [];

      if (responseData.length > 0) {
        // APIが返すグループデータを適切に処理
        processedData = responseData.map((group: any) => {
          // グループ内の商品リストを処理
          const goodsList = Array.isArray(group.goodsList) ? group.goodsList : [];

          // 最低価格とプラットフォームを特定
          let lowestPrice = null;
          let lowestPlatform = '不明';

          // 最低価格を計算
          if (goodsList.length > 0) {
            const lowestProduct = goodsList.reduce((lowest: any, current: any) => 
              current.goodsPrice < lowest.goodsPrice ? current : lowest, goodsList[0]);

            lowestPrice = lowestProduct.goodsPrice;
            lowestPlatform = getPlatformName(lowestProduct.mallType);
          }

          return {
            goodsName: group.goodsName || '不明な商品',
            goodsList: goodsList.map((product: any) => ({
              goodsId: product.goodsId,
              goodsName: product.goodsName,
              goodsPrice: product.goodsPrice,
              goodsLink: product.goodsLink,
              imgUrl: product.imgUrl,
              mallType: product.mallType
            })),
            lowestPrice: lowestPrice,
            lowestPlatform: lowestPlatform
          };
        });

        console.log('Processed grouped data:', processedData); // デバッグ用ログ
      }

      console.log('Final processed data:', processedData); // デバッグ用ログ

      setCompareGroups(processedData);
      setLoading(false);
    } catch (err) {
      setError('検索に失敗しました。後ほど再試行してください');
      setLoading(false);
      console.error(err);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (query.trim()) {
      searchProducts(query);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'ヤフー';
      case 30: return 'タオバオ';
      default: return '不明なプラットフォーム';
    }
  };

  const getPlatformColor = (mallType: number): string => {
    switch (mallType) {
      case 10: return '#bf0000'; // Rakuten Red
      case 20: return '#ff0033'; // Yahoo Red (adjusted)
      case 30: return '#ff5000'; // Taobao Orange
      default: return '#666';
    }
  };

  const getPlatformBadgeClass = (mallType: number): string => {
    switch (mallType) {
      case 10: return 'platform-badge rakuten';
      case 20: return 'platform-badge yahoo';
      case 30: return 'platform-badge taobao';
      default: return 'platform-badge unknown';
    }
  };

  const allProducts: Product[] = compareGroups.flatMap(group => group.goodsList);
  const filteredProducts: Product[] = allProducts.filter(p => {
    const platformOk = selectedPlatforms.length === 0 || selectedPlatforms.includes(p.mallType);
    const minOk = minPrice === '' || (!isNaN(Number(minPrice)) && p.goodsPrice >= Number(minPrice));
    const maxOk = maxPrice === '' || (!isNaN(Number(maxPrice)) && p.goodsPrice <= Number(maxPrice));
    return platformOk && minOk && maxOk;
  });
  const sortedProducts: Product[] = [...filteredProducts].sort((a, b) => {
    if (sortOption === 'price_asc') return a.goodsPrice - b.goodsPrice;
    if (sortOption === 'price_desc') return b.goodsPrice - a.goodsPrice;
    if (sortOption === 'platform') return a.mallType - b.mallType;
    if (sortOption === 'name') return a.goodsName.localeCompare(b.goodsName);
    return 0;
  });
  const togglePlatform = (mt: number) => {
    setSelectedPlatforms(prev => prev.includes(mt) ? prev.filter(x => x !== mt) : [...prev, mt]);
  };

  const totalPages = Math.max(1, Math.ceil(sortedProducts.length / pageSize));
  const currentPageProducts = sortedProducts.slice((currentPage - 1) * pageSize, currentPage * pageSize);
  const goPrev = () => setCurrentPage(p => Math.max(1, p - 1));
  const goNext = () => setCurrentPage(p => Math.min(totalPages, p + 1));
  const setPage = (p: number) => setCurrentPage(Math.min(Math.max(1, p), totalPages));
  const onChangePageSize = (size: number) => { setPageSize(size); setCurrentPage(1); };

  const getSelectKey = (p: Product) => {
    if (p.goodsId) return `id:${p.goodsId}`;
    const base = `${p.mallType}-${p.goodsLink || ''}-${p.goodsName}`;
    return `k:${base}`;
  };
  const isSelected = (key: string) => selectedKeys.includes(key);
  const toggleSelect = (p: Product) => {
    const key = getSelectKey(p);
    setSelectedKeys(prev => prev.includes(key) ? prev.filter(x => x !== key) : [...prev, key]);
  };

  const getSellerFromLink = (url?: string) => {
    if (!url) return '不明な店铺';
    try {
      const u = new URL(url.startsWith('http') ? url : ('https://' + url.replace(/^\/\//, '')));
      return u.hostname.replace(/^www\./, '');
    } catch {
      return '不明な店铺';
    }
  };

  return (
    <div className="compare-page">
      <div className="search-section">
        <h1>商品価格比較</h1>
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)} // 仅更新状态，不触发搜索
            placeholder="商品名を入力して価格比較..."
            className="search-input"
          />
          <button type="submit" className="search-button">搜索</button>
        </form>
      </div>

      {loading && <div className="loading">検索中...</div>}
      {error && <div className="error">{error}</div>}

      {!loading && !error && allProducts.length > 0 && (
        <div className="compare-results">
          <h2>価格比較結果</h2>
          <div className="controls">
            <div className="control-group">
              <label>排序</label>
              <select value={sortOption} onChange={(e) => setSortOption(e.target.value)} className="sort-select">
                <option value="price_asc">价格升序</option>
                <option value="price_desc">价格降序</option>
                <option value="platform">平台</option>
                <option value="name">名称</option>
              </select>
            </div>
            <div className="control-group platform-filters">
              <label>平台</label>
              <div className="platform-options">
                <button type="button" className={selectedPlatforms.includes(10) ? 'active' : ''} onClick={() => togglePlatform(10)}>楽天</button>
                <button type="button" className={selectedPlatforms.includes(20) ? 'active' : ''} onClick={() => togglePlatform(20)}>ヤフー</button>
                <button type="button" className={selectedPlatforms.includes(30) ? 'active' : ''} onClick={() => togglePlatform(30)}>タオバオ</button>
              </div>
            </div>
            <div className="control-group price-filter">
              <label>价格区间</label>
              <div className="price-inputs">
                <input type="number" min="0" placeholder="最小" value={minPrice} onChange={(e) => setMinPrice(e.target.value.replace(/[^0-9]/g, ''))} />
                <span>-</span>
                <input type="number" min="0" placeholder="最大" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value.replace(/[^0-9]/g, ''))} />
              </div>
            </div>
            <div className="control-group view-toggle">
              <label>视图</label>
              <div className="view-options">
                <button type="button" className={viewMode === 'grid' ? 'active' : ''} onClick={() => setViewMode('grid')}>网格</button>
                <button type="button" className={viewMode === 'list' ? 'active' : ''} onClick={() => setViewMode('list')}>列表</button>
              </div>
            </div>
            <div className="control-group pagination-controls">
              <label>分页</label>
              <div className="pagination-inner">
                <button type="button" onClick={goPrev} disabled={currentPage === 1}>上一页</button>
                <span className="page-info">{currentPage} / {totalPages}</span>
                <button type="button" onClick={goNext} disabled={currentPage === totalPages}>下一页</button>
                <select value={pageSize} onChange={(e) => onChangePageSize(Number(e.target.value))}>
                  <option value={12}>12</option>
                  <option value={20}>20</option>
                  <option value={40}>40</option>
                </select>
              </div>
            </div>
          </div>

          {selectedKeys.length > 0 && (
            <div className="compare-table">
              <div className="table-header">已选对比（{selectedKeys.length}）</div>
              <div className="table-body">
                <table>
                  <thead>
                    <tr>
                      <th>名称</th>
                      <th>价格</th>
                      <th>平台</th>
                      <th>店铺</th>
                    </tr>
                  </thead>
                  <tbody>
                    {sortedProducts.filter(p => selectedKeys.includes(getSelectKey(p))).map((p, i) => (
                      <tr key={i}>
                        <td>{p.goodsName}</td>
                        <td>¥{p.goodsPrice.toLocaleString()}</td>
                        <td>{getPlatformName(p.mallType)}</td>
                        <td>{getSellerFromLink(p.goodsLink)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          <div className="compare-group">
            <div className={`product-grid ${viewMode === 'list' ? 'list' : ''}`}>
              {currentPageProducts.map((product, productIndex) => (
                <div 
                    key={productIndex} 
                    className={`product-card ${viewMode === 'list' ? 'list' : ''}`}
                >
                  <div className="select-row">
                    <label className="select-checkbox">
                      <input type="checkbox" checked={isSelected(getSelectKey(product))} onChange={() => toggleSelect(product)} />
                      <span>对比</span>
                    </label>
                    <span className="seller-name">{getSellerFromLink(product.goodsLink)}</span>
                  </div>

                  <div className="product-image-container">
                    <img 
                      src={product.imgUrl || 'https://via.placeholder.com/200x200?text=No+Image'} 
                        alt={product.goodsName} 
                        onError={(e) => {
                          (e.target as HTMLImageElement).src = 'https://via.placeholder.com/200x200?text=Image+Error';
                        }}
                      />
                      <div className={getPlatformBadgeClass(product.mallType)}>
                        {getPlatformName(product.mallType)}
                      </div>
                    </div>

                    <div className="product-card-content">
                      <h4 title={product.goodsName}>{product.goodsName}</h4>

                      <div className="price-section">
                        <span className="currency">¥</span>
                        <span className="amount">{product.goodsPrice.toLocaleString()}</span>
                      </div>

                      <a 
                        href={product.goodsLink} 
                        target="_blank" 
                        rel="noopener noreferrer" 
                        className={`shop-button ${product.mallType === 10 ? 'btn-rakuten' : product.mallType === 20 ? 'btn-yahoo' : ''}`}
                      >
                        立即购买
                      </a>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        </div>
      )}

      {!loading && !error && compareGroups.length === 0 && query && (
        <div className="no-results">関連商品が見つかりません</div>
      )}
    </div>
  );
};

export default ComparePage;
