import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';
import { goodsApi } from '../services/api';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  originalPrice?: number;
  link?: string;
}

const Home: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  // Categories (Kakaku style list)
  const categories = [
    { key: 'smartphone', name: 'Smartphones / SIM', image: 'https://source.unsplash.com/20x20/?smartphone', query: 'スマートフォン' },
    { key: 'laptop', name: 'PCs / Tablets', image: 'https://source.unsplash.com/20x20/?laptop', query: 'パソコン' },
    { key: 'camera', name: 'Cameras', image: 'https://source.unsplash.com/20x20/?camera', query: 'カメラ' },
    { key: 'appliance', name: 'Home Appliances', image: 'https://source.unsplash.com/20x20/?appliance', query: '家電' },
    { key: 'audio', name: 'Audio / Headphones', image: 'https://source.unsplash.com/20x20/?headphones', query: 'イヤホン' },
    { key: 'fashion', name: 'Fashion / Watches', image: 'https://source.unsplash.com/20x20/?watch', query: 'ファッション' },
    { key: 'beauty', name: 'Beauty / Health', image: 'https://source.unsplash.com/20x20/?makeup', query: '美容' },
    { key: 'game', name: 'Games / Toys', image: 'https://source.unsplash.com/20x20/?game', query: 'ゲーム' },
    { key: 'outdoor', name: 'Sports / Outdoor', image: 'https://source.unsplash.com/20x20/?hiking', query: 'スポーツ' },
    { key: 'car', name: 'Cars / Bikes', image: 'https://source.unsplash.com/20x20/?car', query: '車' }
  ];

  const hotTags = ['iPhone 15', 'MacBook Air', 'Sony WH-1000XM5', 'Nintendo Switch', 'RTX 4070', 'iPad Air'];

  useEffect(() => {
    fetchRecommendedProducts();
  }, []);

  const fetchRecommendedProducts = async () => {
    setLoading(true);
    try {
      const response = await goodsApi.getAllGoods(1, 10); // Get top 10 for ranking
      let records = [];
      if (response.data && response.data.data && response.data.data.records) {
        records = response.data.data.records;
      }

      const productsData = records.map((item: any) => ({
        id: item.goodsId,
        name: item.goodsName,
        price: item.goodsPrice,
        imageUrl: typeof item.imgUrl === 'string' ? item.imgUrl : '',
        platform: getPlatformName(item.mallType),
        originalPrice: item.goodsPrice ? Math.floor(item.goodsPrice * 1.1) : undefined,
        link: item.goodsLink
      }));
      setProducts(productsData);
    } catch (err) {
      console.error('Failed to load products:', err);
      // Fallback data
      setProducts(Array(5).fill(0).map((_, i) => ({
        id: i,
        name: 'Sample Product ' + (i+1),
        price: 10000 + i*500,
        imageUrl: '',
        platform: 'Demo',
        originalPrice: 12000
      })));
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/compare?query=${encodeURIComponent(searchQuery)}`);
    }
  };

  const getPlatformName = (mallType: number): string => {
    switch (mallType) {
      case 10: return '楽天';
      case 20: return 'Yahoo!ショッピング';
      case 30: return 'タオバオ';
      case 40: return 'Amazon';
      default: return 'その他';
    }
  };

  return (
    <div className="home">
      <div className="home-container">
        {/* Left Sidebar: Categories */}
        <aside className="home-sidebar">
          <div className="sidebar-box">
            <h3 className="sidebar-header">カテゴリから探す</h3>
            <ul className="sidebar-menu">
              {categories.map((c) => (
                <li key={c.key}>
                  <div 
                    className="sidebar-menu-item"
                    onClick={() => navigate(`/compare?query=${encodeURIComponent(c.query)}`)}
                  >
                    {/* Placeholder icon logic */}
                    <img 
                      src={c.image} 
                      alt="" 
                      onError={(e) => (e.target as HTMLImageElement).style.display = 'none'}
                    />
                    {c.name}
                  </div>
                </li>
              ))}
            </ul>
          </div>

          <div className="sidebar-box">
            <h3 className="sidebar-header">サービス</h3>
            <ul className="sidebar-menu">
              <li><Link to="/compare" className="sidebar-menu-item">価格比較</Link></li>
              <li><Link to="/browse-history" className="sidebar-menu-item">閲覧履歴</Link></li>
              <li><Link to="/my-collection" className="sidebar-menu-item">マイコレクション</Link></li>
            </ul>
          </div>
        </aside>

        {/* Main Content */}
        <main className="home-main">
          {/* Search Bar */}
          <div className="home-search-section">
            <form onSubmit={handleSearch} style={{ display: 'flex', width: '100%', gap: '10px' }}>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="商品名、型番、キーワードを入力..."
                className="home-search-input"
              />
              <button type="submit" className="home-search-btn">検索</button>
            </form>
          </div>

          {/* Hot Keywords */}
          <div className="hot-tags">
            <strong>注目キーワード: </strong>
            {hotTags.map(tag => (
              <a 
                key={tag} 
                href="#" 
                onClick={(e) => { e.preventDefault(); navigate(`/compare?query=${encodeURIComponent(tag)}`); }}
              >
                {tag}
              </a>
            ))}
          </div>

          {/* Ranking / Featured List */}
          <div style={{ marginTop: '20px' }}>
            <div className="section-header">
              <h3 className="section-title">人気商品ランキング</h3>
              <Link to="/compare" style={{ fontSize: '12px', color: '#0033cc' }}>すべて見る &gt;&gt;</Link>
            </div>

            <div className="ranking-list">
              {loading ? (
                <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>ランキング読み込み中...</div>
              ) : (
                products.map((product, index) => (
                  <div key={product.id || index} className="ranking-row">
                    <div className={`rank-num ${index < 3 ? 'top-3' : ''}`}>{index + 1}</div>
                    <div className="rank-img">
                      <img 
                        src={product.imageUrl || 'https://via.placeholder.com/80'} 
                        alt={product.name}
                        onError={(e) => (e.target as HTMLImageElement).src = 'https://via.placeholder.com/80?text=NoImg'}
                      />
                    </div>
                    <div className="rank-info">
                      <Link to={`/compare?query=${encodeURIComponent(product.name)}`} className="rank-title">
                        {product.name}
                      </Link>
                      <div className="rank-meta">
                        {product.platform} | 評価: ★★★★☆ ({(Math.random() * 100).toFixed(0)})
                      </div>
                      <div className="rank-price">
                        ¥{product.price.toLocaleString()}
                        {product.originalPrice && (
                          <span className="rank-price-sub">
                            ({Math.round((1 - product.price / product.originalPrice) * 100)}% OFF)
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Home;
