import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { userApi } from '../services/api';
import './ProfileSetupPage.css';

const ProfileSetupPage: React.FC = () => {
  const [step, setStep] = useState(1); // 1: Avatar, 2: Basic Info
  const [selectedAvatar, setSelectedAvatar] = useState<string>('');
  const [nickname, setNickname] = useState('');
  const [gender, setGender] = useState<'male' | 'female' | 'other'>('other');
  const [birthDate, setBirthDate] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  // 系统提供的头像选项
  const avatars = [
    'https://placehold.co/100x100/6366f1/white?text=A',
    'https://placehold.co/100x100/a855f7/white?text=B',
    'https://placehold.co/100x100/ec4899/white?text=C',
    'https://placehold.co/100x100/f97316/white?text=D',
    'https://placehold.co/100x100/10b981/white?text=E',
    'https://placehold.co/100x100/0ea5e9/white?text=F',
    'https://placehold.co/100x100/8b5cf6/white?text=G',
    'https://placehold.co/100x100/ef4444/white?text=H'
  ];

  useEffect(() => {
    // 设置默认头像
    if (avatars.length > 0 && !selectedAvatar) {
      setSelectedAvatar(avatars[0]);
    }
  }, [avatars, selectedAvatar]);

  const handleNext = () => {
    if (step === 1 && !selectedAvatar) {
      setError('请选择一个头像');
      return;
    }
    setStep(2);
    setError(null);
  };

  const handleBack = () => {
    setStep(1);
    setError(null);
  };

  const handleSubmit = async () => {
    if (!nickname.trim()) {
      setError('请输入昵称');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // 调用API更新用户信息
      const response = await userApi.updateProfile({
        avatar: selectedAvatar,
        nickname,
        gender,
        birthDate: birthDate || null
      });

      if (response.data && response.data.code === 200) {
        // 更新成功，跳转到首页
        navigate('/');
      } else {
        setError(response.data?.message || '更新失败，请稍后重试');
      }
    } catch (err: any) {
      console.error('更新用户信息失败:', err);
      setError(err.response?.data?.message || '更新失败，请稍后重试');
    } finally {
      setLoading(false);
    }
  };

  const handleSkip = () => {
    // 跳过完善资料，直接进入首页
    navigate('/');
  };

  return (
    <div className="profile-setup-page">
      <div className="profile-setup-container">
        <div className="setup-progress">
          <div className={`progress-step ${step >= 1 ? 'active' : ''}`}>
            <span>1</span>
            <span>头像</span>
          </div>
          <div className={`progress-step ${step >= 2 ? 'active' : ''}`}>
            <span>2</span>
            <span>个人信息</span>
          </div>
        </div>

        <h2>
          {step === 1 ? '选择您的头像' : '完善个人信息'}
        </h2>

        {error && <div className="error-message">⚠️ {error}</div>}

        {step === 1 ? (
          <div className="avatar-selection">
            <div className="avatar-grid">
              {avatars.map((avatar, index) => (
                <div
                  key={index}
                  className={`avatar-option ${selectedAvatar === avatar ? 'selected' : ''}`}
                  onClick={() => setSelectedAvatar(avatar)}
                >
                  <img src={avatar} alt={`Avatar ${index + 1}`} />
                </div>
              ))}
            </div>
            <button 
              className="btn-next" 
              onClick={handleNext}
              disabled={!selectedAvatar}
            >
              下一步
            </button>
          </div>
        ) : (
          <div className="profile-form">
            <div className="form-group">
              <label htmlFor="nickname">昵称 *</label>
              <input
                type="text"
                id="nickname"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                placeholder="请输入您的昵称"
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>性别</label>
              <div className="radio-group">
                <label className="radio-option">
                  <input
                    type="radio"
                    name="gender"
                    value="male"
                    checked={gender === 'male'}
                    onChange={() => setGender('male')}
                  />
                  <span>男</span>
                </label>
                <label className="radio-option">
                  <input
                    type="radio"
                    name="gender"
                    value="female"
                    checked={gender === 'female'}
                    onChange={() => setGender('female')}
                  />
                  <span>女</span>
                </label>
                <label className="radio-option">
                  <input
                    type="radio"
                    name="gender"
                    value="other"
                    checked={gender === 'other'}
                    onChange={() => setGender('other')}
                  />
                  <span>其他</span>
                </label>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="birthDate">生日</label>
              <input
                type="date"
                id="birthDate"
                value={birthDate}
                onChange={(e) => setBirthDate(e.target.value)}
                className="form-input"
              />
            </div>

            <div className="form-actions">
              <button 
                type="button" 
                className="btn-back" 
                onClick={handleBack}
              >
                上一步
              </button>
              <button 
                type="button" 
                className="btn-submit" 
                onClick={handleSubmit}
                disabled={loading || !nickname.trim()}
              >
                {loading ? '保存中...' : '保存'}
              </button>
            </div>
          </div>
        )}

        <div className="skip-section">
          <button 
            type="button" 
            className="btn-skip" 
            onClick={handleSkip}
          >
            跳过
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProfileSetupPage;
