import React, { useState, useEffect } from 'react';
import UserSidebar from '../components/UserSidebar';
import './MyTipPage.css';
import { disclosureApi, commonApi } from '../services/api';
import { getImageUrl } from '../utils/urlUtils';

interface Disclosure {
  disclosureId: number;
  title: string;
  content: string;
  link: string;
  disclosurePrice: number;
  imgUrl?: string;
  createTime: string;
  status: number; // 0: Pending, 1: Approved, 2: Rejected
}

const MyTipPage: React.FC = () => {
  const [disclosures, setDisclosures] = useState<Disclosure[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  // Form state
  const [formData, setFormData] = useState<{
    title: string;
    content: string;
    link: string;
    price: string;
    imgUrls: string[];
  }>({
    title: '',
    content: '',
    link: '',
    price: '',
    imgUrls: []
  });

  useEffect(() => {
    loadDisclosures();
  }, []);

  const loadDisclosures = async () => {
    try {
      setLoading(true);
      const res = await disclosureApi.getMyDisclosure(1, 100);
      setDisclosures(res.data.data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await disclosureApi.add({
        title: formData.title,
        content: formData.content,
        link: formData.link,
        disclosurePrice: Number(formData.price),
        imgUrl: formData.imgUrls.join(',')
      });
      alert('投稿しました！審査をお待ちください。'); // Submitted! Please wait for review.
      setShowForm(false);
      setFormData({ title: '', content: '', link: '', price: '', imgUrls: [] });
      loadDisclosures();
    } catch (err) {
      alert('投稿に失敗しました。'); // Failed to submit.
      console.error(err);
    }
  };

  const getStatusText = (status: number) => {
    switch (status) {
      case 0: return '審査中';
      case 1: return '承認済';
      case 2: return '却下';
      default: return '不明';
    }
  };

  const getStatusClass = (status: number) => {
    switch (status) {
      case 0: return 'pending';
      case 1: return 'approved';
      case 2: return 'rejected';
      default: return '';
    }
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.preventDefault();
    e.stopPropagation();

    if (!window.confirm('本当に削除しますか？この操作は取り消せません。')) {
      return;
    }
    try {
      await disclosureApi.delete(id);
      loadDisclosures();
    } catch (err) {
      console.error(err);
      alert('削除に失敗しました。');
    }
  };

  // Japanese Labels
  const labels = {
    title: 'マイページ',
    tabs: {
      submit: '投稿する',
      mySubmissions: '投稿履歴',
      myCollection: 'お気に入り',
      myLikes: 'いいねした投稿'
    },
    submitForm: {
      title: 'タイトル',
      price: '価格',
      link: '商品リンク',
      content: '内容・レビュー',
      image: '画像アップロード',
      submitBtn: '投稿する',
      dragDrop: 'クリックまたはドラッグ＆ドロップで画像をアップロード',
      submitting: '投稿中...'
    },
    status: {
      pending: '審査中',
      approved: '承認済み',
      rejected: '却下'
    },
    empty: 'データがありません'
  };

  const removeImage = (index: number) => {
    setFormData(prev => ({
      ...prev,
      imgUrls: prev.imgUrls.filter((_, i) => i !== index)
    }));
  };

  const handleFileChange = async (files: FileList | null) => {
    if (!files || files.length === 0) return;

    if (formData.imgUrls.length + files.length > 4) {
      alert('画像は最大4枚までアップロードできます');
      return;
    }

    const newUrls: string[] = [];
    // Process files sequentially
    for (let i = 0; i < files.length; i++) {
        const file = files[i];
        if (!file.type.startsWith('image/')) {
          alert('画像ファイルを選択してください');
          continue;
        }

        try {
          const res = await commonApi.upload(file);
          if (res.data && res.data.code === 200) {
            newUrls.push(res.data.data);
          } else {
            alert('アップロード失敗');
          }
        } catch (e) {
          console.error(e);
          alert('アップロードエラー');
        }
    }

    if (newUrls.length > 0) {
        setFormData(prev => ({ ...prev, imgUrls: [...prev.imgUrls, ...newUrls] }));
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    handleFileChange(e.dataTransfer.files);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  return (
    <div className="my-tip-page-wrapper">
      <UserSidebar />
      <div className="my-tip-page">
        <div className="header">
          <h2>マイヒント (投稿管理)</h2>
          <button className="submit-button" onClick={() => setShowForm(!showForm)}>
            {showForm ? 'キャンセル' : '新規投稿'}
          </button>
        </div>

        {showForm && (
          <form onSubmit={handleSubmit} className="tip-form">
            <div className="form-group">
              <label>商品名 / タイトル</label>
              <input 
                type="text" 
                required 
                value={formData.title} 
                onChange={e => setFormData({...formData, title: e.target.value})}
                placeholder="例: iPhone 15 Pro 256GB"
              />
            </div>
            <div className="form-group">
              <label>価格 (円)</label>
              <input 
                type="number" 
                required 
                value={formData.price} 
                onChange={e => setFormData({...formData, price: e.target.value})}
                placeholder="例: 150000"
              />
            </div>
            <div className="form-group">
              <label>商品リンク (URL)</label>
              <input 
                type="url" 
                required 
                value={formData.link} 
                onChange={e => setFormData({...formData, link: e.target.value})}
                placeholder="https://..."
              />
            </div>
            <div className="form-group">
              <label>画像 (最大4枚)</label>
              <div 
                className="image-uploader-area"
                onDrop={handleDrop}
                onDragOver={handleDragOver}
              >
                <div className="image-preview-grid">
                  {formData.imgUrls.map((url, index) => (
                    <div key={index} className="preview-item">
                      <img src={getImageUrl(url)} alt={`Preview ${index}`} />
                      <button 
                        type="button" 
                        className="remove-image-btn"
                        onClick={() => removeImage(index)}
                      >
                        ×
                      </button>
                    </div>
                  ))}

                  {formData.imgUrls.length < 4 && (
                    <div 
                      className="add-image-button"
                      onClick={() => document.getElementById('file-input')?.click()}
                    >
                      <span className="plus-icon">+</span>
                      <span className="add-text">画像を追加</span>
                    </div>
                  )}
                </div>

                {formData.imgUrls.length === 0 && (
                  <p className="upload-hint">画像をドラッグ＆ドロップ、または「+」をクリックしてアップロード</p>
                )}

                <input 
                    id="file-input"
                    type="file" 
                    accept="image/*" 
                    multiple
                    style={{ display: 'none' }} 
                    onChange={(e) => handleFileChange(e.target.files)}
                />
              </div>
            </div>
            <div className="form-group">
              <label>説明 / コメント</label>
              <textarea 
                required 
                value={formData.content} 
                onChange={e => setFormData({...formData, content: e.target.value})}
                placeholder="お得な情報や詳細を記入してください..."
              />
            </div>
            <button type="submit" className="submit-btn">
              投稿する
            </button>
          </form>
        )}

        {loading ? (
          <div>読み込み中...</div>
        ) : disclosures.length === 0 ? (
          <div className="no-tips">投稿履歴がありません</div>
        ) : (
          <div className="tip-list">
            {disclosures.map((item) => (
              <div key={item.disclosureId} className="tip-item">
                <div className="tip-header">
                  <h3>{item.title || '無題'}</h3>
                  <span className={`status status-${getStatusClass(item.status)}`}>
                    {getStatusText(item.status)}
                  </span>
                </div>
                <div className="tip-item-content-wrapper">
                  {item.imgUrl && (
                    <div className="tip-image-wrapper">
                      {/* Show only the first image in the list preview */}
                      <img 
                        src={getImageUrl(item.imgUrl.split(',')[0])} 
                        alt={item.title} 
                        className="tip-image-preview" 
                      />
                      {item.imgUrl.split(',').length > 1 && (
                        <span className="image-count-badge">
                          +{item.imgUrl.split(',').length - 1}
                        </span>
                      )}
                    </div>
                  )}
                  <div className="tip-main-info">
                    <div className="tip-content">{item.content}</div>
                    <div className="tip-price">¥{item.disclosurePrice}</div>
                    <div className="tip-link">
                      <a href={item.link} target="_blank" rel="noopener noreferrer">商品ページへ</a>
                    </div>
                  </div>
                </div>
                <div className="tip-footer">
                  <span className="time">{new Date(item.createTime).toLocaleString()}</span>
                  <button 
                    onClick={(e) => handleDelete(e, item.disclosureId)} 
                    className="delete-btn"
                  >
                    削除
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MyTipPage;
