import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';
import { goodsApi } from '../services/api';

interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
  platform: string;
  originalPrice?: number;
  link?: string;
}

const Home: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  // Japanese Labels
  const labels = {
    heroTitle: 'ÊúÄÂÆâÂÄ§„ÇíÊé¢„Åù„ÅÜ',
    searchPlaceholder: 'ÂïÜÂìÅ„ÇíÊ§úÁ¥¢ (‰æã: iPhone 15, MacBook)...',
    searchBtn: 'Ê§úÁ¥¢',
    recommended: '„Åä„Åô„Åô„ÇÅÂïÜÂìÅ',
    viewAll: '„Åô„Åπ„Å¶Ë¶ã„Çã',
    compare: 'ÊØîËºÉ„Åô„Çã',
    loading: 'Ë™≠„ÅøËæº„Åø‰∏≠...',
    categories: '„Ç´„ÉÜ„Ç¥„É™„Éº'
  };

  // Categories (Kakaku style list)
  const categories = [
    { key: 'smartphone', name: '„Çπ„Éû„Éº„Éà„Éï„Ç©„É≥„ÉªSIM', icon: 'üì±', query: '„Çπ„Éû„Éº„Éà„Éï„Ç©„É≥' },
    { key: 'laptop', name: '„Éë„ÇΩ„Ç≥„É≥„Éª„Çø„Éñ„É¨„ÉÉ„Éà', icon: 'üíª', query: '„Éë„ÇΩ„Ç≥„É≥' },
    { key: 'camera', name: '„Ç´„É°„É©', icon: 'üì∑', query: '„Ç´„É°„É©' },
    { key: 'appliance', name: 'ÁîüÊ¥ªÂÆ∂Èõª', icon: 'üè†', query: 'ÂÆ∂Èõª' },
    { key: 'audio', name: '„Ç™„Éº„Éá„Ç£„Ç™„Éª„Éò„ÉÉ„Éâ„Éõ„É≥', icon: 'üéß', query: '„Ç§„É§„Éõ„É≥' },
    { key: 'fashion', name: '„Éï„Ç°„ÉÉ„Ç∑„Éß„É≥„ÉªÊôÇË®à', icon: 'üëî', query: '„Éï„Ç°„ÉÉ„Ç∑„Éß„É≥' },
    { key: 'beauty', name: 'ÁæéÂÆπ„ÉªÂÅ•Â∫∑', icon: 'üíÑ', query: 'ÁæéÂÆπ' },
    { key: 'game', name: '„Ç≤„Éº„É†„Éª„Åä„ÇÇ„Å°„ÇÉ', icon: 'üéÆ', query: '„Ç≤„Éº„É†' },
    { key: 'outdoor', name: '„Çπ„Éù„Éº„ÉÑ„Éª„Ç¢„Ç¶„Éà„Éâ„Ç¢', icon: '‚õ∫', query: '„Çπ„Éù„Éº„ÉÑ' },
    { key: 'car', name: 'Ëªä„Éª„Éê„Ç§„ÇØ', icon: 'üöó', query: 'Ëªä' }
  ];

  const hotTags = ['iPhone 15', 'MacBook Air', 'Sony WH-1000XM5', 'Nintendo Switch', 'RTX 4070', 'iPad Air'];

  useEffect(() => {
    fetchRecommendedProducts();
  }, []);

  const fetchRecommendedProducts = async () => {
    setLoading(true);
    try {
      // ‰ºòÂÖà‰ΩøÁî®ÁúüÂÆûÂπ≥Âè∞Êï∞ÊçÆÔºöÂØπÁÉ≠Èó®ÂÖ≥ÈîÆËØçÊâßË°åÊØî‰ª∑ÊêúÁ¥¢Âπ∂Ê±áÊÄªÊúÄ‰Ωé‰ª∑ÂïÜÂìÅ
      const querySeeds = ['iPhone 15', 'MacBook Air', 'Nintendo Switch', 'Sony WH-1000XM5', 'iPad Air'];
      const collected: Product[] = [];

      for (const q of querySeeds) {
        try {
          const res = await goodsApi.compareGoods(q);
          if (res.data && res.data.code === 200 && Array.isArray(res.data.data)) {
            // Â±ïÂºÄÊØè‰∏™ÂàÜÁªÑÁöÑÊúÄ‰Ωé‰ª∑ÂïÜÂìÅ
            res.data.data.forEach((group: any) => {
              const list = Array.isArray(group.goodsList) ? group.goodsList : [];
              if (list.length > 0) {
                // ÈÄâÊã©ÊúÄ‰Ωé‰ª∑Êù°ÁõÆ
                const cheapest = list.reduce((min: any, cur: any) => {
                  const mp = typeof cur.goodsPrice === 'number' ? cur.goodsPrice : Number(cur.goodsPrice) || 0;
                  const minp = typeof min.goodsPrice === 'number' ? min.goodsPrice : Number(min.goodsPrice) || 0;
                  return mp > 0 && (minp === 0 || mp < minp) ? cur : min;
                }, list[0]);
                if (cheapest && cheapest.goodsName && cheapest.goodsPrice && cheapest.goodsLink) {
                  collected.push({
                    id: cheapest.goodsId || Math.floor(Math.random() * 1e9),
                    name: cheapest.goodsName,
                    price: cheapest.goodsPrice,
                    imageUrl: typeof cheapest.imgUrl === 'string' ? cheapest.imgUrl : '',
                    platform: getPlatformName(cheapest.mallType),
                    originalPrice: cheapest.goodsPrice ? Math.floor(cheapest.goodsPrice * 1.1) : undefined,
                    link: cheapest.goodsLink
                  });
                }
              }
            });
          }
        } catch (e) {
          // Âçï‰∏™ÂÖ≥ÈîÆËØçÂ§±Ë¥•Êó∂ÁªßÁª≠ÂÖ∂‰ªñÂÖ≥ÈîÆËØç
          console.warn('compareGoods failed for', q, e);
        }
        if (collected.length >= 10) break;
      }

      // Â¶ÇÊûúÊØî‰ª∑Êï∞ÊçÆ‰∏çË∂≥ÔºåÂõûÈÄÄÂà∞ÂçïÂπ≥Âè∞Ôºà‰πêÂ§©ÔºâÊêúÁ¥¢
      if (collected.length < 5) {
        try {
          const fallbackRes = await goodsApi.searchGoods('iphone');
          const items = (fallbackRes.data && fallbackRes.data.data) ? fallbackRes.data.data : [];
          items.forEach((item: any) => {
            collected.push({
              id: item.goodsId,
              name: item.goodsName,
              price: item.goodsPrice,
              imageUrl: typeof item.imgUrl === 'string' ? item.imgUrl : '',
              platform: getPlatformName(item.mallType),
              originalPrice: item.goodsPrice ? Math.floor(item.goodsPrice * 1.1) : undefined,
              link: item.goodsLink
            });
