import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { userApi } from '../services/userApi';
import './LoginPage.css';

interface LoginFormData {
  phone: string; // 使用phone字段兼容后端API
  password: string;
}

const LoginPage: React.FC = () => {
  const [formData, setFormData] = useState<LoginFormData>({
    phone: '',
    password: ''
  });
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // 清除之前的错误信息
    if (error) setError(null);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // 调用真实API - 使用新的login端点
      const response = await userApi.login({
        username: formData.phone, // 使用username字段，但保持变量名不变以兼容现有代码
        password: formData.password
      });

      console.log('Login response data:', response.data); // 调试日志

      if (response.data && response.data.code === 200) {
        // 登录成功，跳转到主页
        const token = response.data.token || response.data.data?.token;
        if (token) {
          localStorage.setItem('token', token);
        }
        navigate('/');
      } else {
        // 尝试从响应中获取错误消息
        const errorMessage = response.data?.message || response.data?.msg || '登录失败，请重试';
        setError(errorMessage);
      }
    } catch (err: any) {
      // 处理不同的错误情况
      if (err.response) {
        // 服务器响应错误
        switch (err.response.status) {
          case 401:
            setError('用户名或密码错误');
            break;
          case 404:
            setError('用户不存在');
            break;
          case 500:
            setError('服务器错误，请稍后重试');
            break;
          default:
            setError(err.response.data?.message || '登录失败，请重试');
        }
      } else if (err.request) {
        // 网络错误
        setError('网络连接失败，请检查网络设置');
      } else {
        // 其他错误
        setError('登录过程中发生错误，请重试');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2>用户登录</h2>
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="phone">用户名或邮箱</label>
            <input
              type="text"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="请输入用户名或邮箱"
              required
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">密码</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="请输入密码"
              required
              className="form-input"
            />
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={loading}
          >
            {loading ? '登录中...' : '登录'}
          </button>
        </form>

        <div className="login-footer">
          <p>还没有账号？ <Link to="/register" className="link">立即注册</Link></p>
          <Link to="/" className="link">返回首页</Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
